content:
[[module css]]
@import url(http://ja.scp-wiki.net/component:spc-theme/code/1);
[[/module]]

[[code type="css"]]
/*
    SPC Theme
    [2018 Wikidot Theme]
    Based on Aelanna's Sigma 9 and Greenwolf's Third Law and djkaktus' Darkbody Theme
    Edited by PeppersGhost for the Doomsday Contest 
*/

#page-content a:visited {
    color: #083f77;
}

/* HEADER */
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(http://ja.scp-wiki.net/local--files/component:spc-theme/SPCLogo50.png) 10px 35px no-repeat;
}

#header h1 a {
    content: "SPC財団";
}

#header h1 a::before {
    content: "SPC財団";
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}

#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "サメ殴りセンター";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

div#container-wrap {
    background: url(http://ja.scp-wiki.net/local--files/component:spc-theme/SPCWrapper.jpg) top left repeat-x;
}
/* Source: https://www.flickr.com/photos/telemax/2818520042 */

/* SIDEBAR */
#side-bar img {
  width:13px;
  height:13px;
  box-sizing:border-box;
  padding-left: 13px;
  background: url(http://ja.scp-wiki.net/local--files/component:spc-theme/FirstIconTransparent.png) left top no-repeat;
}
/* Source: https://commons.wikimedia.org/wiki/File:Noun_project_758.svg */

#side-bar .heading {
    color: #0e7dcd;
    border-bottom: solid 1px #0e7dcd;
}

#side-bar div.menu-item.inactive a {
    color: #0b65c0;
}

#side-block a:visited {
    color: #083f77;
}

#side-bar div.menu-item a:visited {
    color: #083f77;
}

#side-bar div.menu-item .sub-text {
    color: #0b65c0;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #0b65c0;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #0b65c0;
}

.interwiki_title {
    color: #0b65c0;
}

.interwiki_entry a {
    color: #0b65c0;
}

/* LINKS */

a {
    color: #0c499c;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #0c499c;
    text-decoration: none;
    background: transparent;
}

/* RATING MODULE */
div.page-rate-widget-box .rate-points {
    background-color: #132639;
    border: solid 1px #0b65c0;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: 132639;
    border-top: solid 1px #0b65c0;
    border-bottom: solid 1px #0b65c0;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: 132639;
    color: #0b65c0;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #132639;
    color: #0b65c0;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #132639;
    border: solid 1px #0b65c0;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #0b65c0;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #132639;
    color: #fffff0;
    text-decoration: none;
}

/* PAGE TITLEs */
h1, #page-title {
    color: #0c499c;
}

/* SEARCH BAR */
#search-top-box-input {
    border: solid 1px #0b65c0;
    background-color: #132639;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #0b65c0
    background-color: #132639;
}
#search-top-box-form input[type=submit] {
    border: solid 1px #0b65c0;
    background-color: #132639;
    color: #0b2447;
    background: linear-gradient(to bottom, #2373cd,#319ec2,#183291);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #0b65c0;
    color: #0b2447;
    background-color: #132639;
    background: linear-gradient(to bottom, #2373cd,#319ec2,#183291);
}

/* Interwiki */

#side-bar .scpnet-interwiki-wrapper iframe.scpnet-interwiki-frame {
    -webkit-filter: hue-rotate(220deg) saturate(92%) brightness(84%);
    filter: hue-rotate(220deg) saturate(92%) brightness(84%);
}

[[/code]]
