[[module CSS]]

.creditRate {
float: unset !important;
margin-bottom: unset !important;
}

#page-title {
text-align: center;
}

#content-wrap {
max-width: 460px;
padding: 1rem;
border: solid 1px rgb(200, 200, 200);
}

a.disruptionHeader {
color: white !important;
transition-duration: 1s;
}

 a.disruptionHeader:hover{
     animation: grad 5s linear infinite;
     -webkit-animation: grad 5s linear infinite;
     -moz-animation: grad 5s linear infinite;
     background-image:
        linear-gradient(-45deg, #55CDFC, #F7A8B8, #FFF, #F7A8B8, #55CDFC, #F7A8B8, #FFF, #F7A8B8, #55CDFC, #F7A8B8, #FFF, #F7A8B8, #55CDFC, #F7A8B8, #FFF, #F7A8B8, #55CDFC);
     background-size: 200%;
}

 @-webkit-keyframes grad{
     0%{
          background-position: 100% 0%;
    }
     100%{
          background-position: 15% 100%;
    }
}

 @-moz-keyframes grad{
     0%{
          background-position: 100% 0%;
    }
     100%{
          background-position: 15% 100%;
    }
}

 @keyframes grad{
     0%{
          background-position: 100% 0%;
    }
     100%{
          background-position: 15% 100%;
    }
}
.creditButtonStandalone p a{
    background: hsl(0, 0%, 40%) !important;
}
[[/module]]

