[[include component:jstyles]]
[[module CSS]]
.mtfIcon {
  display: inline-block;
  width: 30px;
  text-align: center; 
  border-radius: 3px;
  line-height: 100%;
  padding: 2px 0;
}
.mtfIcon .letter {
  position: relative;
  top: -1px;
  font-weight: bold; 
  line-height: 100%;
}
.mtfIcon .number {
  font-size: 60%;
  line-height: 100%;
}
.mtf-legend {
  text-align: center;
}
.mtf-legend p {
  margin: 5px 0;
}
.mtf-legend .legend-entry {
  display: inline-block;
  margin: 0 20px;
  width: 220px;
  text-align: left;
}
[[/module]]

