[[module css]]
@import url('https://fonts.googleapis.com/css?family=Noto+Sans+JP|Noto+Serif+JP:700');
@import url(http://scp-sandbox-3.wikidot.com/local--code/component:todaysdate/7);
div#container-wrap {
    background: url(http://kaktuskontainer.wdfiles.com/local--files/stormbreath-hub-page/body_bg.png) top left repeat-x;}

/* テキストエフェクト */
.neon {
  font-family:'Noto Sans JP';
  color:#8ec3f9;
  text-shadow: 0 0 5px #f0f, 0 0 1px #00f, 0 0 1px #f00, 0 0 1px #00f,
    0 0 5px #0ff, 0 0 10px #0ff, 0 0 1px #f0f, 0 0 1px #fff;
}
/* メインタイトル */
#header {background-image: url(http://scpsandboxcn.wdfiles.com/local--files/feitag/go.png);}
#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Noto Serif JP', serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "オネイロイ・ウエスト";
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-family: 'Noto Sans JP';
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '総 て は 唯 の 夢';
    color: #fff;
    font-style: italic;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

sup {
  vertical-align: top;
  position: relative;
  top: -0.5em;
}

/* リンクの色・ぼかし */
.menu a:link{color: #9900FF}
.menu a:visited{color: #9900FF}
.menu a:hover{color: #cd00bd}

.menub a:link{color: #2200AA}
.menub a:visited{color: #2200AA}
.menub a:hover{color: #cd00bd}


.underline-on-hover a:link{color: black}

.blur {color: transparent; text-shadow: 0 0 6px rgba(0,0,0,.3); }

/* 評価モジュール */
div.page-rate-widget-box .rate-points {
  background-color: #61006c;
  border: solid 1px #61006c;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #f0f8ff;
  border-top: solid 1px #61006c;
  border-bottom: solid 1px #61006c;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #006c00;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #61006c;
  color: #f0f8ff;
}
.page-rate-widget-box .cancel {
  background-color: #61006c;
  border: solid 1px #61006c;
}
.page-rate-widget-box .cancel a {
  color: #f0f8ff;
}
.page-rate-widget-box .cancel a:hover {
  background: #dccdea;
  color: #f0f8ff;
}

/* 偽ボタン */
.button {
    display: inline-block;
    text-align: center;
    vertical-align: middle;
    padding: 6px 43px;
    border: 0px solid #4088b1;
    border-radius: 0px;
    background: #607075;
    background: -webkit-gradient(linear, left top, left bottom, from(#607075), to(#01070a));
    background: -moz-linear-gradient(top, #607075, #01070a);
    background: linear-gradient(to bottom, #607075, #01070a);
    font: normal normal bold 15px arial;
    color: #ffffff;
    text-decoration: none;
}
.button:hover,
.button:focus {
    border: 0px solid ##5bc2fd;
    background: #73868c;
    background: -webkit-gradient(linear, left top, left bottom, from(#73868c), to(#01080c));
    background: -moz-linear-gradient(top, #73868c, #01080c);
    background: linear-gradient(to bottom, #73868c, #01080c);
    color: #ffffff;
    text-decoration: none;
}
.button:active {
    background: #3a4346;
    background: -webkit-gradient(linear, left top, left bottom, from(#3a4346), to(#01070a));
    background: -moz-linear-gradient(top, #3a4346, #01070a);
    background: linear-gradient(to bottom, #3a4346, #01070a);
}

/* 偽入力欄 */
.typeborder{
border: 1px solid rgba(180,180,180,1);
padding: 4px 150px 4px 4px;
font-size: 1.2em;
font-weight: bold;
}
