[[include :scp-jp-sandbox2:inc:jstyles]]
[[module css]]

/*
    ODSS Theme
    [2018 Wikidot Theme]
    Created by shu_yabiyabi and 7happy7
    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css?family=Noto+Serif+JP');

/* general */
body,
h1 {
font-family: 'Noto Serif JP',verdana,arial,helvetica,sans-serif;
}
div#container-wrap {
background: url(http://shitake-crude-production.wikidot.com/local--files/odss-theme/odss-body.png) top left repeat-x;
}
#page-title,
a,
h1 {
color: #a60000;
}
blockquote {
border: inset 2px #ececec;
padding: 0 1em;
background-color: #fff;
}

/* #header */
#header {
text-align: center;
background: none!important;
}
#header h1 {
display: inline-block;
position: absolute;
left: 0;
right: 0;
margin: 0 auto;
width: 100%;
overflow: hidden;
}
#header h1 a {
display: inline-block;
left: 50%;
padding: 80px 0px 25px;
line-height: 0px;
max-height: 0px;
color: transparent;
background: transparent;
font-family: 'Noto Serif JP', serif;
font-size: 180%;
font-weight: 100;
text-decoration: none;
text-shadow: none;
transform: scale(0.65, 1);
}
#header h1 a span {
display: none;
}
#header h1 a::before {
content: "OYAKUNIN DAISENSEI SAMURAI";
color: rgba(26, 26, 26, 0.95);
text-shadow:
    rgba(230, 230, 230, .8) 1.5px 0px,  rgba(230, 230, 230, .8) -1.5px 0px,
    rgba(230, 230, 230, .8) 0px -1.5px, rgba(230, 230, 230, .8) 0px 1.5px,
    rgba(230, 230, 230, .8) 1.5px 1.5px , rgba(230, 230, 230, .8) -1.5px 1.5px,
    rgba(230, 230, 230, .8) 1.5px -1.5px, rgba(230, 230, 230, .8) -1.5px -1.5px,
    rgba(230, 230, 230, .8) 0.75px 1.5px,  rgba(230, 230, 230, .8) -0.75px 1.5px,
    rgba(230, 230, 230, .8) 0.75px -1.5px, rgba(230, 230, 230, .8) -0.75px -1.5px,
    rgba(230, 230, 230, .8) 1.5px 0.75px,  rgba(230, 230, 230, .8) -1.5px 0.75px,
    rgba(230, 230, 230, .8) 1.5px -0.75px, rgba(230, 230, 230, .8) -1.5px -0.75px;
bottom: 0.5em;
position: relative;
white-space: nowrap;
}
#header h2 {
display: inline-block;
position: absolute;
bottom: 1.6em;
left: 0;
right: 0;
margin: 0 auto;
transform: scale(0.65, 1);
font-size: 350%;
}
#header h2 span {
font-size: 0px;
padding: 5px;
}
#header h2:after {
content: "SINOBI";
font-family: 'Noto Serif JP', serif;
padding: 22px 0;
color: rgba(166, 0, 0, 0.95);
text-shadow: 1px 1px 1px rgba(255, 255, 255, .8);
white-space: pre;
}

/* #search-top-box */
#search-top-box input.empty {
color: #666666;
}
#search-top-box input.empty:hover{
color: #fff;
}
#search-top-box-input {
background-color:#fff;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
background-color:#666666;
}
#search-top-box-form input[type=submit] {
background:#fff;
color:#666666;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
background:#666666;
color:#fff;
}

/* #login-status */
#login-status {
background-color:#ffffffdd;
}
#login-status a {
color:#7d7d7d;
}

/* #top-bar */
#top-bar ul > li > a {
color: #fff;
}
#top-bar ul li:first-child a {
border-left: none!important;
}
#top-bar ul li:last-child a {
border-right: none!important;
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
background: #fff;
color: #1A1A1A;
border-left: solid 1px rgba(64,64,64,.2);
border-right: solid 1px rgba(64,64,64,.2);
}

/* .yui-navset */
.yui-navset {
overflow: hidden;
}
.yui-navset li{
line-height: 141%;
word-break:break-all;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
background-color: rgba(102,102,102,0.02);
display: block;
text-align: center;
border: 2px solid rgba(26,26,26,0.7);
border-bottom: none;
border-radius: 4px 4px 0 0 / 4px 4px 0 0;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
background-color: transparent ! important; /* tab background */
background-image: none;
border-color: #4D4D4D;
color: #333;
transition: 0.125s;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
background-image: none;
}
.yui-navset .yui-nav a em {
border-color: transparent;
box-shadow: 0px 1px 2px rgba(0,0,0,0.5);
background-color: rgba(255,255,255,0.05);
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
background-image: none;
background-color: rgba(102,102,102,0.02);
border-color:#4D4D4D;
color: #a60000;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
background-color: rgba(102,102,102,0.02);
box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
border-color:#4D4D4D;
}
.yui-navset .yui-content scp-image-block img {
position: relative;
z-index: 100;
}
.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
background-color: rgba(102,102,102,0.02);
border: 2px solid rgba(26,26,26,0.7);
border-top: none;
border-radius: 0 0 4px 4px / 0 0 4px 4px;
}
.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected,
.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
margin: 0;
padding: 0;
}

/* rate module */
.page-rate-widget-box span {
display: inline-flex;
justify-content: center;
align-items: center;
height: 18px;
box-sizing: border-box;
}
.page-rate-widget-box .rate-points {
font-family: 'Hiragino Kaku Gothic Pro', 'ヒラギノ角ゴ Pro W3', Meiryo, メイリオ, Osaka, 'MS PGothic', arial, helvetica, sans-serif;
font-weight: bolder;
font-size: 11px;
}
.page-rate-widget-box .cancel {
padding: 0 2px 0 3px;
}
.page-rate-widget-box .cancel a {
font-family: 'FontAwesome';
font-weight: 400;
font-size: 0;
color: #fff6f0;
padding: 3px 5px;
height: 12px;
}
.page-rate-widget-box .cancel a:hover {
color: #966;
}
.page-rate-widget-box .cancel a:before {
content: '\f00d';
font-size: 10px;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
display: inline-flex;
justify-content: center;
align-items: center;
font-size: 0;
font-family: 'FontAwesome';
font-weight: 400;
padding: 2px 5px;
height: 16px;
box-sizing: border-box;
}
.page-rate-widget-box .rateup a:before {
content: '\f067';
font-size: 10px;
}
.page-rate-widget-box .ratedown a:before {
content: '\f068';
font-size: 10px;
}
.page-rate-widget-box {
display: inline-flex;
margin: 0;
}

/* #side-bar */
#side-bar .side-block {
background-color: rgba(102,102,102,0.06)!important;
border-color: #666;
box-shadow: 0 2px 6px rgba(102,102,102,0.06);
}
#side-bar .side-block a {
color: #a60000;
}
.scpnet-interwiki-wrapper {
filter: hue-rotate(5deg) brightness(97%);
}

/* @media */
@media (max-width: 330px) {
#header h1 a::before {
left: -2em!important;
}
}
@media (min-width: 330px) and (max-width: 350px) {
#header h1 a::before {
left: -1.8em!important;
}
}
@media (min-width: 350px) and (max-width: 400px) {
#header h1 a::before {
left: -1.4em!important;
}
}
@media (min-width: 400px) and (max-width: 450px) {
#header h1 a::before {
left: -0.5em!important;
}
}
@media (max-width: 479px) {
#search-top-box-input {
display: none;
}
}
@media (max-width: 689px) {
#header h1 a {
line-height: 0.3em!important;
}
#header h1 a::before {
content: "OFFICER DOCTOR\A SOLDIER"!important;
white-space: pre!important;
bottom: 0.9em!important;
}
#header h2 {
bottom: 1.3em!important;
}
}
@-moz-document url-prefix() {
@media (max-width: 689px) {
#header h1 a {
line-height: 0.5em!important;
}
}
}
@media (max-width: 767px) {
#side-bar {
background-color: #fff;
}
#side-bar:target .close-menu {
opacity:0;
}
}


/* ////////////////////////////// */


/*
ruby
jstyles (http://scp-jp-sandbox2.wikidot.com/inc:jstyles)
by Nanimono Demonai
