[[include :scpko:theme:dustjacket]]
[[module css]]
#page-title {
  font-size: 2.0em !important;
  font-weight: bold !important;
}

.fancyborder {
  width: 12.8cm;
  margin: 0 auto;
  word-break: break-all;
  text-align: justify;
  line-height: 160%;
  font-size: 10pt;
}

.fancyborder h1 {
  text-align: center;
}

.ko-footer {
  margin-bottom: -3.5em;
}

.ko-footer hr {
  display: none !important;
}

/* Custom Assets by EstrellaYoshte, from Penumbra BHL theme */
    .limit {
        margin-bottom: -1rem;
    }

    .anchor {
        position: -webkit-sticky;
        position: sticky;
        height: 0;
        top: 0;
        z-index: 9999;
    }

    .sidebox {
        background-color: rgb(var(--very-light-gray-monochrome));
        border: 0.1875rem outset rgb(var(--medium-accent));
        border-top: solid 0.125rem rgb(var(--swatch-primary));
        padding: .14rem 1rem .14rem;
        margin-top: 0;
        margin-bottom: 0.5rem;
        width: calc((120vw - (var(--body-width-on-desktop) + 10rem))/2);
        max-height: calc(100vh - 18rem);
        position: absolute;
        top: 0;
        left: 103.5%;
        z-index: 5;
        overflow: auto;
        box-sizing: border-box;
    }

    .hovertip {
        color: rgb(var(--swatch-text-general));
    }

    @media (max-width: 80.625rem) {
        .sidebox {
            width: auto;
            max-width: 50vw;
            color: rgba(0, 0, 0, 0);
            background-color: rgb(var(--very-light-gray-monochrome));
            box-shadow: 0 0 0 0 rgb(var(--swatch-primary));
            border: none;
            right: calc(((100vw - 1em - var(--body-width-on-desktop)) / 2) * -1);
            left: initial;
            pointer-events: none;
            transition: max-width 0.2s ease-in-out 0.1s, color 0.2s ease-in-out 0.1s, box-shadow 0.5s ease-in-out 0.1s, background-color 0s ease-in-out 0s;
            overflow-x: hidden;
            overflow-y: hidden;
            scrollbar-width: thin;
        }

        .sidebox::-webkit-scrollbar {
            width: 0.5em;
        }

        .sidebox * {
            opacity: 0;
            transition: opacity 0.2s ease-in-out 0.1s;
        }

        .sidebox::before,
        .sidebox::after {
            content: " ";
            position: absolute;
            pointer-events: all;
            right: 0;
        }

        .sidebox::before {
            top: calc(50% - 0.75rem);
            width: 0;
            height: 0;
            border-top: 0.75rem solid transparent;
            border-bottom: 0.75rem solid transparent;
            border-right: 0.75rem solid rgb(var(--swatch-primary));
            transition: border 0.1s ease-in-out 0.1s;
            z-index: 10;
        }

        .sidebox::after {
            top: 0;
            max-width: 0.75rem;
            width: 100%;
            height: 100%;
            box-shadow: 0.125rem 0 0 0 rgb(var(--swatch-primary));
            max-height: calc(100vh - 18rem);
            background-color: rgb(var(--black-monochrome));
            z-index: -1;
            transition: box-shadow 0.5s ease-in-out 0.1s, max-width 0.5s ease-in-out 0.1s, border-top-left-radius 0.5s ease-in-out 0.1s, border-bottom-left-radius 0.5s ease-in-out 0.1s;
        }

        .sidebox:hover,
        .sidebox:active {
            color: rgb(var(--swatch-text-general));
            background-color: rgb(var(--black-monochrome));
            pointer-events: all;
            overflow-y: scroll;
            transition: max-width 0.5s ease-in-out 0.2s, color 0.5s ease-in-out 0.2s, box-shadow 0.5s ease-in-out 0.1s, background-color 0s ease-in-out 0.6s, right 0.5s ease-in-out 0.1s;
        }

        .sidebox:hover *,
        .sidebox:active * {
            opacity: 1;
            transition: opacity 0.5s ease-in-out 0.2s;
        }

        .sidebox:hover::before,
        .sidebox:active::before {
            border-top: 0vw solid transparent;
            border-bottom: 0vw solid transparent;
        }

        .sidebox:hover::after,
        .sidebox:active::after {
            box-shadow: 0 -0.125rem 0 0 rgb(var(--swatch-primary));
            right: 0;
            max-width: 100%;
            border-top-left-radius: 0rem;
            border-bottom-left-radius: 0rem;
        }
    }

    @media only screen and (max-width:56.25rem) {
        :root {
            --body-width-on-desktop: 100vw;
            --header-h1-font-size: calc(2rem + (2.5 - 2) * ((100vw - 18.750rem) / (60 - 18.750)));
            --header-h2-font-size: var(--base-font-size);
        }

        #header h1,
        #header h1 a,
        #header h1 a::before {
            top: 0.5rem;
        }

        #header h2,
        #header h2 a,
        #header h2 a::before {
            top: 0.35rem;
        }

        .anchor {
            top: calc(var(--topbar-height-on-mobile) + 0.75rem);
        }

        .sidebox,
        .sidebox:hover,
        .sidebox:active {
            right: calc(((100vw - (100% - 3rem))/2) * -1 + 1.5em);
        }

        .sidebox::before,
        .sidebox::after {
            content: " ";
            position: absolute;
            pointer-events: all;
            right: 0;
        }

        .blockquote,
        [class*="blockquote"],
        blockquote,
        div.blockquote {
            margin: 0;
        }
    }

    @media only screen and (min-width: 48.0625rem) {

        #search-top-box-form input[type="submit"],
        #search-top-box-form input[type="submit"]:focus,
        #search-top-box-form input[type="submit"]:hover {
            border-left: 0.0625rem solid rgba(var(--swatch-primary-darkest), 0);
        }

        #search-top-box-form input[type="submit"] {
            background: rgba(var(--swatch-primary-darkest), 0.5);
        }
    }
}
