@import url('https://fonts.googleapis.com/css2?family=Libre+Franklin:ital,wght@0,100..900;1,100..900&amp;amp;display=swap');

:root {
    --basalt-secondary-color: 0, 0, 0;
    --basalt-tertiary-color: 0, 0, 0;

    --basalt-overtone: 164, 155, 150;
    --basalt-undertone: 164, 155, 150;
    --basalt-bright-element-color: 164, 155, 150;
    --basalt-dark-element-color: 164, 155, 150;

    --link-color: 164, 155, 150;
    --link-visited-color: 164, 155, 150;
    --link-newpage-color: 164, 155, 150;

    --top-bar-link-color-hover: 255, 255, 255;
    --login-status-text-color-hover: 255, 255, 255;
    --side-bar-link-color-hover: 255, 255, 255;
    --side-bar-link-background-hover: 0,0,0;
    --selection-text-color: 0, 0, 0;

    --header-border-width: 0rem;
    --title-size: 1.25rem;
    --subtitle-size: 0rem;
    --side-bar-button-height: 50vh;
    --user-button-width: 2rem;
    --header-logo-size: 67.5%;

    --basalt-UI-dark-palette: 0,0,0;
    --top-bar-link-background-hover: 164, 155, 150;

}

#header h1 a::before {
    display: none;
}

#header h1 a span::after {
    margin-bottom: 0;
}

#container #header {
    background: none;
    position: absolute;
    box-shadow: unset;
    backdrop-filter: none;
}

div #container-wrap {
    background-color: #000000;
    background: url(https://scpsandboxcn.wikidot.com/local--files/leebrfj/PublicDomainConBackground.png);
    background-size: contain;
}

#main-content {
    margin-top: 0;
}
#page-content {
    padding-top: 0;
}

#page-content > p:nth-child(-n+4) {
    display: none;
}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    max-width: 100dvw !important;
}

.wideboxinterior {
    max-width: 100% !important;
    margin: auto;
}

.wideboxinterior2 {
    width: 50% !important;
    margin: auto;
}

.widebox .wideboxinterior2 {
    font-family: "Libre Franklin", serif;
    font-size: 1rem;
}

.widebox .wideboxinterior2 .blockquote {
    margin: 2rem auto;
    max-width: 100% !important;
    outline: 3px solid #5A5551;
    outline-offset: -10px;
    padding: 50px !important;
    background-color: #12110F;
    border: none;
}

.widebox .wideboxinterior2 h1 {
    color: #D2CDCA;
    font-size: 3rem;
}

.wideboxinterior:before {
    content: "";
    position: absolute;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    top: calc(100dvh);
    height: calc(100dvh);
    max-width: 100dvw !important;
    background-image: linear-gradient(0deg, transparent 0%, transparent 95vh, rgb(0,0,0) 100vh, transparent 100%);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    pointer-events: none;
    z-index: 1;
}

.headerimage {
    position: relative;
    height: calc(100dvh);
    background-image: url(http://olicus.wikidot.com/local--files/pdcontest2025/AFfSO3V.jpeg);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
}

.headerimage .headertoptext {
    position: relative;
    display: block;
    margin: auto;
    top: 10dvh;
    width: 45%;
}

.headerimage .headerbottext {
    position: relative;
    display: block;
    margin: auto;
    bottom: -55dvh;
    width: 30%;
}

.page-source {
    color: white;
}

h1, #page-options-container, .licensebox .collapsible-block-folded a, .page-watch-options a {
    color: white;
}

#action-area, .licensebox .collapsible-block-content {
    background: #0D0D0F;
    padding: 10px;
}

.licensebox .collapsible-block-link {
    background: #0D0D0F;
    padding: 10px;
}

/* Piece of shit sidebar */

#top-bar div.mobile-top-bar>.open-menu {
     visibility:visible;
     opacity: 0.6;
     transition: opacity 0.2s;
}

#top-bar div.mobile-top-bar>.open-menu:hover {
    opacity: 1;
}

#side-bar {
     margin-top:0;
     height:100%
}

#side-bar, #side-bar:is(:hover, :focus-within):not(:target) {
     left:calc(var(--side-bar-width) * -1)
}

#side-bar::after, #side-bar::before {
     display:none
}

#side-bar:target {
     left:0;
     width:var(--side-bar-width);
     z-index:10
}

#side-bar a.close-menu, #side-bar:target a.close-menu {
     display:block;
     pointer-events:none;
     position:fixed;
     top:0;
     right:0;
     left:auto;
     width:100%;
     min-width:3.75rem;
     height:100%;
     margin-left:0;
     opacity:0;
     z-index:-1;
     transition:width var(--side-bar-transition),opacity var(--side-bar-transition)   
}

#side-bar:target a.close-menu {
     width:calc(100% - var(--side-bar-width));
     pointer-events:auto;
     opacity:1;
}

/* Mobile */

@media only screen and (max-width: 1020px) {
    #container #header {
        background: black;
        position: sticky;
    }
    .headerimage {
        height: calc(70dvh);
        background-image: url(http://olicus.wikidot.com/local--files/pdcontest2025/AFfSO3V.jpeg);
        overflow-x: clip;
    }
    .headerimage .headertoptext {
        position: relative;
        display: block;
        margin: auto;
        top: 10dvh;
        height: 20%;
        width: auto;
    }
    .headerimage .headerbottext {
        position: relative;
        display: block;
        margin: auto;
        bottom: -43dvh;
        height: 7%;
        width: auto;
    }
    .wideboxinterior2 {
        width: 95% !important;
        margin: auto;
    }
    .widebox .wideboxinterior2 .blockquote {
        margin: 0rem auto;
        padding: 30px !important;
    }
    .widebox .wideboxinterior2 h1 {
        color: #D2CDCA;
        font-size: 2rem;
    }
    .wideboxinterior:before {
        background-image: none;
    }
}

