[[include :scp-wiki:theme:blankstyle]]
[[module css]]
body{
    --barColour: #102485;
    --linkColour: #FFFFFF;
}

:root{
  --header-title: "SCP-5140";
  --header-subtitle: "EVEREST";
}
#header h1 a::before, #header h2::before {
  color: #ededed;
}
body {
    background-image: linear-gradient(
        to bottom,
        #142da8, #102485 150px,
        #102485 150px, #dedede 190px,
        #dedede 190px, #dedede 100%);
    background-repeat: no-repeat;
}

div#extra-div-1 {
    background-image: url(http://scp-wiki.wikidot.com/local--files/peak/everest-icon);
    filter: opacity(50%);
}

#top-bar, #top-bar a {
    color: #ededed;
}

#login-status,
#login-status a {
    color: #ededed;
}

a {
    color: #0645AD;
}
 a:visited {
     color: #0B0080;
}
#side-bar a {
     color: black
}

#side-bar a:visited {
     color: black;
}

#top-bar ul li ul {
    border: solid 1px black;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid white;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: white;
    color: #0024d9;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #0024d9;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #0024d9;
    color: white; /* top bar hover background color */
}

#header-extra-div-1,
#header-extra-div-2,
#header-extra-div-3 {
    top: 0;
}

#page-content .info-container {
    --link-color: rgb(var(--swatch-text-general));
}

#page-content .info-container .translation_block {
    display: none;
}

#page-content .info-container .collapsible-block-link,
#page-content .info-container:hover .collapsible-block-link {
    line-height: 1.6em;
}

#page-content .info-container .collapsible-block-folded,
#page-content .info-container .collapsible-block-unfolded-link {
    display: grid;
}

#page-content .info-container .collapsible-block-folded .collapsible-block-link,
#page-content .info-container .collapsible-block-link {
    -webkit-mask: unset;
            mask: unset;
    background: transparent;
    grid-column: 1;
    grid-row: 1;
    opacity: 0;
}

#page-content .info-container .collapsible-block-folded::before,
#page-content .info-container .collapsible-block-unfolded-link::before {
    --wght: 800;
    content: "Elenee FishTruck";
    color: #FFFFFF;
    font-family: Inter, Arimo, Verdana, Geneva, "Helvetica Neue", "Helvetica", Arial, sans-serif;;
    display: block;
    pointer-events: none;
    grid-column: 1;
    grid-row: 1;
    line-height: 2.75ex;
}
[[/module]]

