[[module css]]

#page-title {
  display: none;
}

div#container {
  margin-top: 40vh;
  margin-top: 40dvh;
}

div#content-wrap {
  margin-top: 0;
}

#container::before {
  position: absolute;
  display: flex;
  align-items: flex-end;
  justify-content: center;
  top: -40vh;
  top: -40dvh;
  content: "å¹¿å‘Š";
  font-size: 80%;
  color: #9eccab;
  height: 40vh;
  height: 40dvh;
  width: 100dvw;
  background: url(https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla-skips/parallel_ad.jpg) top 2vh center no-repeat;
  background: url(https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla-skips/parallel_ad.jpg) top 2dvh center no-repeat;
  background-color: black;
  background-size: auto 35vh; 
  background-size: auto 35dvh; 
}

@media (min-width: 1024px) {
  div#content-wrap {
    grid-template-columns: 1fr; 
    column-gap: 0;
    padding-top: 0;
  }
  div#side-bar { display: none; }
  div#main-content { width: 100%; }
}

@media (max-width: 1024px) {
  #container::before {
    top: -25vh;
    top: -25dvh;
    background-size: 95dvw auto; 
    height: 25vh;
    height: 25dvh;
  }
  div#container { 
    margin-top: 25vh; 
    margin-top: 25dvh; 
  }
}

.head-area {
  background-color: #546d7c40;
  padding-top: 3rem;
}

.search-box {
  position: relative;
  width: 70%;
  border: 3px solid white;
  background-color: #546d7c;
  border-radius: 1.5rem;
  font-size: 1.5rem;
  color: #ccc;
  padding-left: 1rem;
  margin: 1rem auto;
}

.search-box p {
  margin: 0;
}

.search-box::after {
  content: "";
  position: absolute;
  top: 50%;
  right: 0;
  width: 1.5rem;
  height: 1.5rem;
  background-color: white;
  mask-image: var(--search-icon-mask);
  transform: translate(-50%, -50%);
}

.link-area {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  column-gap: 1rem;
  text-align: center;
  width: 80%;
  margin: auto;
  background-color: #546d7c40;
  background-image: url("https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla-skips/food.png");
  padding: 1rem;
  margin-block: 1rem;
  border-radius: 2rem;
}

@media (max-width: 1024px) {
  .link-area {
    grid-template-columns: 1fr;
    column-gap: 0;
    row-gap: 1rem;
  }
}

.link-item {
  background-color: #546d7c;
  border-radius: 0.8rem;
  overflow: hidden;
  color: white;
  transition: filter 0.2s ease-in-out;
}

.link-item:hover {
  filter: brightness(1.2);
  cursor: pointer;
}

.link-item p {
  margin: 0;
}

.link-img {
  position: relative;
}

.link-img img {
  width: 100%;
  aspect-ratio: 3 / 2;
}

.link-img::before {
  position: absolute;
  content: attr(data-title);
  left: 0;
  bottom: 0;
  background-color: #ffdc8c;
  color: black;
  padding: 0.1rem 0.5rem;
  font-size: 120%;
  font-weight: bold;
}

.link-text {
  margin: 0.5em;
  font-size: 150%;  
  text-align: start;
}

.wiki-area {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 2fr;
  column-gap: 1rem;
  color: white;
  text-align: center;
  background-color: #546d7c;
  padding: 1rem 3rem;
  margin-top: 3rem;
}

@media (max-width: 1024px) {
  .wiki-area {
    grid-template-columns: 1fr;
    column-gap: 0;
    row-gap: 1rem;
  }
}

.wiki-area a {
  color: white;
}

.wiki-area .button {
  background-color: #ffc73b;
  color: black;
  width: fit-content;
  height: fit-content;
  margin: auto;
  padding: 0.25em 0.5em;
}

.wiki-area .button p {
  margin: 0;
}

.wiki-area .end {
  display: flex;
  align-items: center;
  justify-content: center;
}

.wiki-area .end::after {
  content: "â–¶";
  color: black;
  font-size: 150%;
  background-color: #ffc73b;
  padding: 0.5rem 0.5rem;
  margin: auto;
  cursor: pointer;
}

/* -------------------- */

.notice-area {
  display: grid;
  grid-template-columns: 5fr 2fr;
  column-gap: 1rem;
  margin-block: 3rem;
}

@media (max-width: 1024px) {
  .notice-area {
    grid-template-columns: 1fr; 
    column-gap: 0;
    row-gap: 1rem;
  }
}

.error-area {
  background-color: white;
  padding: 1rem 2rem;
  font-size: 120%;
}

.error-head {
  display: grid;
  grid-template-columns: 1fr 5fr;
  column-gap: 1rem;
}

@media (max-width: 1024px) {
  .error-head {
    grid-template-columns: 1fr; 
    column-gap: 0;
  }
}

.error-note {
  align-self: end;
}

.error-note p {
  margin: 0;
}

.inpage-adv .real-adv {
  background-color: #536c7bc0;
  padding: 1rem;
  color: white;
}

.games {
  display: grid;
  grid-template-columns: 3fr 2fr;
  background-color: white;
  color: black;
  cursor: pointer;
}

.info {
  align-self: end;
  justify-self: end;
  padding: 0.5em;
  text-align: end;
}

.info::before {
  content: attr(data-title);
  background-color: green;
  color: white;
  padding: 0.1em 0.2em;
  align-self: center;
  margin-bottom: 0.5em;
}

.inpage-adv .adv {
  background-color: #536c7bc0;
  margin: 0.5rem auto;
  padding: 0.2em 1em;
  width: fit-content;
  color: white;
}

.info p, .adv p {
  margin: 0;
}

.linear-adv {
  position: relative;
  height: 15vh;
  height: 15dvh;
  width: min(85dvw, 40em);
  margin: 3rem auto;
}

.linear-adv .switch {
  position: absolute;
  right: 0.2rem;
  top: 0.2rem;
}

.abswitch p {
  margin: 0;
}

.abswitch span {
  display: block;
  font-size: 0;
  height: 0;
}

.linear-adv .switch a.collapsible-block-link {
  background-color: white;
}

.linear-adv .real-adv {
  height: 100%;
  width: 100%;
}

.linear-adv .fake-adv, .linear-adv .final-scene {
  display: none;
  background-color: white;
  height: 100%;
  width: 100%;
  color: gray;
  font-size: 120%;
}

.linear-adv .reply-area {
  display: none;
  background-color: white;
  height: 100%;
  width: 100%;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch1, .collapsible-block-unfolded[style="display: block;"] span.switch1)  .real-adv {
  display: none;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch1, .collapsible-block-unfolded[style="display: block;"] span.switch1)  .fake-adv {
  display: block;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch1, .collapsible-block-unfolded[style="display: block;"] span.switch1) .switch {
  left: 0.2em;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch1, .collapsible-block-unfolded[style="display: block;"] span.switch1) .switch a.collapsible-block-link {
  color: gray;
}

.buttons {
  display: grid;
  grid-template-columns: 1fr 1fr; 
  row-gap: 1rem;
}

.buttons .blue a.collapsible-block-link {
  background-color: #4285f5;
  color: white;
  max-width: 80%;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 3px;
  margin-inline: auto;
  box-shadow: 0 2px 2px gray;
  padding-block: 0.2em;
}

.buttons .blue a.collapsible-block-link:hover, .buttons .blue a.collapsible-block-link:focus {
  background-color: #3275e5;
  text-decoration: none;
}

.buttons .white a {
  background-color: #ffffff;
  color: gray;
  max-width: 80%;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 3px;
  margin-inline: auto;
  box-shadow: 0 2px 2px gray;
  padding-block: 0.2em;
}

.buttons .white a.collapsible-block-link {
  color: #4285f5;
}

.buttons .white a:hover, .buttons .white a:focus {
  background-color: #f5f5f5;
  text-decoration: none;
}

.buttons p {
  margin: 0;
}

@media (max-width: 1024px) {
  .linear-adv {
    height: 25vh;
    height: 25dvh;
  }
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch2, .collapsible-block-unfolded[style="display: block;"] span.switch2) .fake-adv {
  display: none;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch2, .collapsible-block-unfolded[style="display: block;"] span.switch2) .switch {
  display: none;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch2, .collapsible-block-unfolded[style="display: block;"] span.switch2) .reply-area {
  display: block;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch3, .collapsible-block-unfolded[style="display: block;"] span.switch3) .reply-area {
  display: none;
}

.linear-adv:has(.collapsible-block-unfolded[style=""] span.switch3, .collapsible-block-unfolded[style="display: block;"] span.switch3) .final-scene {
  display: block;
}

