.ad0 .ad-container {
  position: fixed;
  left: 0;
  top: 0;
  height: 100%;
  width: 100%;
  background-color: #000c;
  z-index: 100;
}

.ad0 p {
  margin: 0;
}

.ad0 .collapsible-block-unfolded .collapsible-block-link {
  opacity: 0;
  position: fixed;
  color: white;
  left: 92%;
  width: 6%;
  text-align: center;
  top: -50%;
  z-index: 110;
  animation: ten-app linear 10s forwards;
}

.ad0 .ad-area {
  position: absolute;
  left: 50%;
  top: 50%;
  width: 80%;
  aspect-ratio: 8 / 5;
  transform: translate(-50%, -50%);
  background-color: transparent;
  z-index: 102;
}

.ad0 .img-bg {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  background: url(https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla-skips/las-vegas.jpg) center no-repeat;
  background-size: cover;
  filter: blur(2px);
}

.ad0 .ad-area .trump {
  position: absolute;
  right: 0;
  bottom: 10%;
  width: 30%;
  animation: trump-anime 10s linear forwards infinite;
}

@keyframes trump-anime {
0%, 10%, 90%, 100% { right: 0; }
25%, 75% { right: 5%;}
}

.ad0 .trump .says {
  position: absolute;
  right: 60%;
  bottom: 70%;
  width: 80%;
  opacity: 0;
  animation: word-anime 10s linear forwards infinite;
} 

.ad0 .trump .says img {
  width: 100%;
}

@keyframes word-anime {
0%, 30%, 70%, 100% { opacity: 0; }
40%, 60% { opacity: 1;}
}

.ad0 .intro {
  position: absolute;
  right: 5%;
  bottom: 3%;
  font-size: 150%;
  font-weight: 800;
  animation: trumpintro-anime 5s linear forwards infinite;
  text-shadow: 0 0 4px black;
  transform: scaleY(1.5);
  font-style: italic;
}

@keyframes trumpintro-anime {
0%, 100% {color: gold;}
50% {color: silver;}
}

.ad0 .countdown {
  opacity: 1;
  position: fixed;
  color: white;
  left: 92%;
  width: 6%;
  text-align: center;
  top: 5%;
  z-index: 110;
  animation: ten-disp linear 10s forwards;
}

.ad0 .cd::after {
  content: "9";
  font-weight: bold;
  animation: ten-cd linear 10s forwards;
}

@keyframes ten-disp {
0%, 99% { opacity: 1; }
100% { opacity: 0; }
}

@keyframes ten-app {
0% {
  opacity: 0;
  top: -50%;
}
98% {
  top: -50%;
}
99% {
  opacity: 0;
  top: 5%;
}
100% {
  opacity: 1;
  top: 5%;
}
}

@keyframes ten-cd {
0% { content: "9"; }
10% { content: "8"; }
20% { content: "7"; }
30% { content: "6"; }
40% { content: "5"; }
50% { content: "4"; }
60% { content: "3"; }
70% { content: "2"; }
80% { content: "1"; }
90%, 100% { content: "0"; }
}

@media (max-width: 1024px) {
  .ad0 .intro { font-size: 80%; }
  .ad0 .collapsible-block-unfolded .collapsible-block-link {
    left: 55%;
    width: 40%;
  }
  .ad0 .countdown {
    left: 55%;
    width: 40%;
  }
}

/* this is taken from theme:kitchen */

.ad0 .roller {
  --roller-left: 2%;
  --roller-img: url(https://scpsandboxcn.wdfiles.com/local--files/sharia-vanilla-skips/roll1.png);
  position: absolute;
  overflow: hidden;
  height: 100%;
  top: 0;
  left: var(--roller-left);
  width: 10%;
}

.ad0 .roller::before {
  content: "";
  position: absolute;
  left: 0;
  bottom: 0;
  width: 100%;
  aspect-ratio: 300 / 6756;
  background-image: var(--roller-img);
  background-repeat: no-repeat;
  background-position: center;
  background-size: 100% auto;
  animation: linear rolling-burgers 20s infinite;
}

.ad0 .rev::before {
  bottom: unset;
  top: 0;
  animation: linear rolling-burgers-rev 20s infinite;
}

@keyframes rolling-burgers {
0% { bottom: 0; }
100% { bottom: -250%; }
}

@keyframes rolling-burgers-rev {
0% { top: 0; }
100% { top: -250%; }
}

.ad0 .title {
  position: absolute;
  left: 18%;
  top: 15%;
  width: 80%;
  height: auto;
  animation: linear title-shake 4s infinite;
}

@keyframes title-shake {
0%, 50%, 100% { transform: rotate(0deg) scale(1); }
25% { transform: rotate(-3deg) scale(1.1); }
75% { transform: rotate(3deg) scale(0.9); }
}

[[/module]] 

