[[module CSS]]
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: none;
    opacity: 0.8;
}

#header{
    background: url(null)
}
#header:before{
display:block;
position: absolute;
width: 100px;
height: 100px;
top: 22%;
left:2%;
z-index: -1;
content: "";
background:url(http://scpsandbox2.wdfiles.com/local--files/collab%3Adraft-swap-hub/ogspin);
background-repeat: no-repeat;
background-size:100px 100px;
background-position: 0px 0px;
z-index: 201;
  animation: spinny1 9s ease infinite;
}

/*
CC BY-SA 3.0
spinny
ikr_4185
 */
@keyframes spinny1 {
    0% {
      -webkit-transform: rotate(0deg) scale(1);
      transform: rotate(0deg) scale(1);
    }
    100% {
      -webkit-transform: rotate(360deg) scale(1);
      transform: rotate(360deg) scale(1);
    }
}


#header:after{
display:block;
position: absolute;
width: 100px;
height: 100px;
top: 22%;
left:2%;
z-index: -2;
content: "";
background:url(http://topia.wdfiles.com/local--files/niconews/spin2.png);
background-repeat: no-repeat;
background-size:100px 100px;
background-position: 0px 0px;
z-index: 200;
}

/*
CC BY-SA 3.0
spinny
ikr_4185
 */
@keyframes spinny2 {
    0% {
      -webkit-transform: rotate(0deg) scale(1);
      transform: rotate(0deg) scale(1);
    }
    50% {
      -webkit-transform: rotate(60deg) scale(1);
      transform: rotate(60deg) scale(1);

    }
    100% {
      -webkit-transform: rotate(360deg) scale(1);
      transform: rotate(360deg) scale(1);
    }
}
#header h1 a::before {
    content: "你的鸽子，我来炖";
    color: white;
}
#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "你领到了哪一只？";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #ffffff;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);

#side-bar .side-block {
    padding: 10px;
    border: 3px double #9c3111;
    border-radius: 0;
    box-shadow: 0 2px 6px rgba(102,0,0,.5);
    background: #fff;
}
[[/module]]
