[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Georama:ital,wght@1,700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Georama:wght@900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Archivo:ital,wght@1,300&display=swap');

.main{
    text-decoration: none!important;
}

.main:hover {
    text-decoration: none!important;
}
.main:visited {     
    color: white;
}

.name {
    font-family: 'Georama', 'LXGW WenKai', sans-serif;
    font-size: 2.5em;
    font-style: italic;
    font-weight: 700;
}

.tale {
    color: #b0d8f7!important;
    font-family: 'Georama', '仓耳渔阳体W05', sans-serif;
    font-size: 3em;
    font-style: italic;
    font-weight: 700;
}

.subtitle {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    color: white;
    FONT-SIZE: 1.3em;
    position: relative;
    font-style: italic;
    top: -0.7em;
}

.quote {
    font-family: 'Archivo', 'LXGW WenKai',  sans-serif;
    color: white;
    FONT-SIZE: 1em;
    font-style: italic;
    top: -2em;
}

.info {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    FONT-SIZE: 1.1em;
    font-style: italic;
    color:white;
    position: relative;
    top: -1.2em;
}

.rate {
    text-align: center;
    position: relative;
    top: -1.5em;
    color: #0a3e66;
    text-shadow: 1px 1px 0px black;
}

@media (max-width: 480px) {
  .rate {
    position: relative;
    top: -3.5em;
  }
}

.main {
    display: block;
    height: 18em;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
    margin: -1em;
}

.capon {
    text-align:center;
    font-size: 1.1em;
    letter-spacing: 1.15em;
    margin-top: -1em;
    margin-left: 1em;
    color: #051c2e;
}

.art {
    background-position: 25% 40%;
    background-size: 140%;
    background-color: #54abee;
    background-blend-mode: multiply;
    transition: all 200ms ease-in-out;
    color: white! important;
}

.art:hover {
    color: #b0d8f7!important;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    text-shadow: 1.5px 1.5px 0px #051c2e;
    mix-blend-mode: none;
}

.art:hover .subtitle {
    color: #e3f1fc!important;
}

.header {
  font-family: "Georama", '仓耳渔阳体W05', sans-serif;
  font-size: 5em;
  color: #b0d8f7;
  text-shadow: 4px 4px 0px #0a3e66;
}

.proposal {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    FONT-SIZE: 1.1em;
    position: relative;
    top: -1.2em;
    font-style: italic;
    color: white;
}

.art:hover .proposal {
    font-family: 'Archivo', sans-serif;
    FONT-SIZE: 1.1em;
    position: relative;
    top: -1.2em;
    font-style: italic;
    color: gold;
}

@media only screen and (max-width: 26.5625rem) {
.name {
    font-family: 'Georama', 'LXGW WenKai', sans-serif;
    font-size: 3vmax;
    font-style: italic;
    font-weight: 700;
}

.tale {
    color: #b0d8f7!important;
    font-family: 'Georama', '仓耳渔阳体W05', sans-serif;
    font-size: 3vmax;
    font-style: italic;
    font-weight: 700;
}

.subtitle {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    color: white;
    FONT-SIZE: 1.5vmax;
    position: relative;
    font-style: italic;
    top: -1.3vmax;
}

.quote {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    color: white;
    FONT-SIZE: 1vmax;
    font-style: italic;
}

.rate {
    text-align: center;
    position: relative;
    top: -1.5vmax;
    color: #0a3e66;
    text-shadow: 1px 1px 0px black;
}

@media (max-width: 480px) {
  .rate {
    position: relative;
    top: -3.5vmax;
  }
}

.main {
    display: block;
    height: 20vmax;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
    margin: -2.5vmin;
}

.capon {
    text-align:center;
    font-size: 0.9em;
    letter-spacing: 0.8em;
    margin-top: -1em;
    margin-left: 1em;
    color: #051c2e;
}

.art {
    background-position: 25% 40%;
    background-size: 140%;
    background-color: #54abee;
    background-blend-mode: multiply;
    transition: all 200ms ease-in-out;
    color: white! important;
}

.art:hover {
    color: #b0d8f7!important;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    text-shadow: 1.5px 1.5px 0px #051c2e;
    mix-blend-mode: none;
}

.art:hover .subtitle {
    color: #e3f1fc!important;
}

.header {
  font-family: "Georama", '仓耳渔阳体W05', sans-serif;
  font-size: 8vmin;
  color: #b0d8f7;
  text-shadow: 4px 4px 0px #0a3e66;
}

.info {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    FONT-SIZE: 1vmax;
    position: relative;
    top: -1.9vmax;
    font-style: italic;
    color:white;
}


.proposal {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    FONT-SIZE: 1vmax;
    background-color: transparent;
    border: none;
    width:90%;
    top: -1.9vmax;
    margin: 10px auto;
    padding: 5px auto;
}

.art:hover .proposal {
    font-family: 'Archivo', 'LXGW WenKai', sans-serif;
    FONT-SIZE: 1vmax;
    position: relative;
    top: -1.9vmax;
    font-style: italic;
    color: gold;
}
}

#header::before {
        opacity: 1;
}

.widebox div {
    position: relative;
    top: 70%;
}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(40em);
    max-width: 100dvw !important;
    background-image: url(http://esperanzacai.wikidot.com/local--files/files/p-13);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
    margin-top: -3.70em;
}

#header {
   --login-line-divider-color: var(--bluedark);
   --login-username-color: var(--bluedark);
   --login-myaccount-color: var(--bluedark);
   --login-myaccount-hover-color: var(--red);
   --login-myaccount-underline-color: var(--bluedark);
   --login-myaccount-hover-bg-color: var(--bluedark);
   --login-arrow-color: var(--bluedark);
}

