[[module css]]

.plaguepjp {
  --color1: #051c2e;
  --color2: #030e17;

  font-family: "Georama", sans-serif;
  font-size: 8em;
  background: repeating-linear-gradient(
    90deg,
    var(--color1),
    var(--color1) 30px,
    var(--color2) 30px,
    var(--color2) 60px
  );
  background-clip: text;
  color: transparent;
  -webkit-background-clip: text;
  animation: 30s linear 0s infinite move;
}

@keyframes move {
  from {
    background-position: 0px;
  }
  to {
    background-position: 1000px;
  }
}

@media only screen and (max-width: 26.5625rem) {

.plaguepjp {
   --color1: #051c2e;
   --color2: #030e17;

  font-family: "Georama", sans-serif;
  font-size: 14vmin;
  background: repeating-linear-gradient(
    90deg,
    var(--color1),
    var(--color1) 30px,
    var(--color2) 30px,
    var(--color2) 60px
  );
  background-clip: text;
  color: transparent;
  -webkit-background-clip: text;
  animation: 30s linear 0s infinite move;
}

@keyframes move {
  from {
    background-position: 0px;
  }
  to {
    background-position: 1000px;
  }
}
}
