[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Archivo:ital,wght@1,400&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Lexend:wght@100..900&display=swap');

/* Widebox 8000 Image */
.widebox div {
    position: relative;
    top: 70%;
}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(45em);
    max-width: 100dvw !important;
    background-image: url(
http://scptestwiki.wikidot.com/local--files/jawn-hub-pics/sealmain.webp);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
    margin-top: -2.70em;
    background-color: rgb(var(--dark-accent));
}

/* Sentence under PlaguePJP image */
.capon {
    text-align:center;
    font-size: 1.2em;
    letter-spacing: .1em;
    margin-top: -1em;
    margin-left: 1em;
    color: #051c2e;
}


/* Container of SCP articles (.art) */
.main{
    text-decoration: none!important;
}

.main:hover {
    text-decoration: none!important;
}
.main:visited {     
    color: white;
}

.main {
    display: block;
    height: 18em;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
    margin: -1em;
}

/* Main content of SCP article box */
.art {
    background-position: 25% 40%;
    background-size: 140%;
    background-color: #54abee;
    background-blend-mode: soft-light;
    transition: all 200ms ease-in-out;
    color: black! important;
    padding: -30px;
}

.art:hover {
    transition: all 200ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    mix-blend-mode: none;
    background-color: none;
    text-shadow: #051c2e 0px 0 3px;
}

/* span class for mirrored divs */
.rightsub {
float: right;
margin-right: 1em;
text-align: right;
}

/* SCP numerical designation */
.name {
    font-family: 'Lexend', sans-serif;
    font-size: 2.5em;
    font-weight: 700;
    letter-spacing: -1px;
    position: relative;
    color: black;
}

.art:hover .name {
    color: #b0d8f7!important;
    transition: all 25ms ease-in-out;
}


/* SCP title */
.subtitle {
    font-family: 'Lexend', sans-serif;
    font-weight: 200;
    color: black;
    FONT-SIZE: 1.5em;
    position: relative;
    font-style: italic;
    top: -0.7em;
}

.art:hover .subtitle {
    color: white!important;
}

/* Other information in .art div */
.info {
    font-family: 'Lexend', sans-serif;
    FONT-SIZE: 1.1em;
    font-style: italic;
    color: black;
    position: relative;
    top: -1.2em;
    font-weight: 200;
    line-height: 21px;
}

.art:hover .info {
    color: white!important;
}

.proposal {
    font-family: 'Lexend', sans-serif;
    font-weight: 200;
    FONT-SIZE: 1.1em;
    position: relative;
    top: -1.2em;
    font-style: italic;
    color: black;
    line-height: 21px;
}

.art:hover .proposal {
    color: gold;
}

/* Rating module alterations */
.rate {
    text-align: center;
    position: relative;
    top: -1.5em;
    color: #0a3e66;
    text-shadow: 1px 1px 0px black;
}


.tale {
    color: #b0d8f7!important;
    font-family: 'Lexend', sans-serif;
    font-weight: 900;
    font-size: 2.3em;
    font-style: italic;
    font-weight: 700;
    line-height: -1.5em;
    margin-bottom: -1.5em;
}

/* Mobile Parity */
@media only screen and (max-width: 26.5625rem) {
    .main {
      height: 20vmax;
      padding-left: 0.75rem;
      border-radius: 3px;
      margin: -2.5vmin;
    }

    .name {
      font-size: 3vmax;
    }

    .subtitle {
      font-size: 1.5vmax;
      top: -1.3vmax;
    }

    .info {
      font-size: 1vmax;
      top: -1.7vmax;
      line-height: 11px;
    }

    .proposal {
      font-size: 1vmax;
      top: -2.5vmax;
    }

    .rate {
      position: relative;
      top: -3.5em;
     }

    .tale {
       font-size: 3vmax;
       line-height: 25px;
     }
}

@media only screen and (max-width: 440px) {
    .main {
      height: 18vmax;
      padding-left: 0.75rem;
      border-radius: 3px;
      margin: -2.5vmin;
    }

    .name {
      font-size: 3vmax;
    }

    .subtitle {
      font-size: 1.5vmax;
      top: -1.3vmax;
    }

    .info {
      font-size: 1vmax;
      top: -1.7vmax;
      line-height: 11px;
    }

    .proposal {
      font-size: 1vmax;
      top: -2.5vmax;
    }

    .rate {
      position: relative;
      top: -3.5em;
     }

    .tale {
       font-size: 3vmax;
       line-height: 25px;
     }
}
