[[include :scp-jp:theme:blankstyle]]
[[include :scp-jp:component:jstyles]]
[[include :scp-wiki:component:fade-in speed=1]]
[[module css]]
@import url(http://dog-punch.wikidot.com/local--files/tes:10035/kugiri.css);

:root{
  --header-title: "SITE-81KK";
  --header-subtitle: "近畿は近忌の土地である";
}

div#extra-div-1 {
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A3995762-456-bzu5/81kk.png);
    filter: opacity(20%);
}

.headbox h2, .headbox h3{
  margin: 0 0 .5em;
  text-align: center;
}
.headbox-underline {
  border-bottom: solid 4px #5F4894;
}
.headbox h2:first-letter, .headbox h3:first-letter{
  font-size: 1.4em;
  color: #5F4894;
}

/* https://pote-chil.com/html-maker/quote */
.quote1 {
    max-width: 500px;
    position: relative;
    padding: 2.5em 2.5em 2em 3em;
    color: #333333;
}

.quote1::before,
.quote1::after {
    disPlay: inline-block;
    position: absolute;
    width: 4em;
    height: 4em;
    content: '';
}

.quote1::before {
    top: 0;
    left: 0;
    border-top: 3px solid #5f4894;
    border-left: 3px solid #5f4894;
    background-image: url('data:image/svg+xml;charset=utf8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%2024%2024%22%3E%3Cpath%20fill%3D%22%235f4894%22%20d%3D%22M21.8%2012H18V9c0-1.7%201.3-3%203-3h.4c.6%200%201.1-.5%201.1-1.1V2.6c0-.6-.5-1.1-1.1-1.1H21c-4.1%200-7.5%203.4-7.5%207.5v11.2c0%201.2%201%202.2%202.2%202.2h6c1.2%200%202.2-1%202.2-2.2v-6C24%2013%2023%2012%2021.8%2012zM8.2%2012H4.5V9c0-1.7%201.3-3%203-3h.4C8.5%206%209%205.5%209%204.9V2.6c0-.6-.5-1.1-1.1-1.1h-.4C3.4%201.5%200%204.9%200%209v11.2c0%201.2%201%202.2%202.2%202.2h6c1.2%200%202.2-1%202.2-2.2v-6c.1-1.2-.9-2.2-2.2-2.2z%22%2F%3E%3C%2Fsvg%3E');
    background-position: top 35% left 35%;
    background-size: 1.5em;
    background-repeat: no-repeat;
}

.quote1::after {
    bottom: 0;
    right: 0;
    border-bottom: 3px solid #5f4894;
    border-right: 3px solid #5f4894;
}

.quote1:first-letter{
  font-size: 1.6em;
  font-weight: bold;
  color: #5F4894;
}

/* https://scp-wiki.wikidot.com/theme:flopstyle */
 .card-block{ /* ---- Decorative Quote Block ---- */
     margin: 1em 0;
     padding: .6em 1.2em;

     background: #f4f4f4;
     border-left: 8px solid var(--accentColor);
     border-radius: .48em;
}

 blockquote hr, div.blockquote hr, .alt-blockquote hr, .lightweight hr, .card-block hr{
     border-color: hsl(0, 0%, 67%);
}

.report-box{ /* ---- Report Block ---- */
     margin: 1em 0;
     padding: 0 1em;

     background-color: hsl(0, 0%, 100%);
     border: medium solid hsl(0, 0%, 5%);
}
 .report-box hr{
     border-color: hsl(0, 0%, 5%);
}

 .realistic-shadow{ /* ---- Better Shadows ---- */
     margin: 1em 0 2em 0;
     box-shadow:
          0 1px 1px hsla(0, 0%, 0%, .23),
          0 2px 2px hsla(0, 0%, 0%, .18),
          0 4px 4px hsla(0, 0%, 0%, .15),
          0 8px 8px hsla(0, 0%, 0%, .13);
}

/*http://1nu.wikidot.com/scp*/
#page-content .collapsible-block-link {
    disPlay: flex;
    height: 3.5em;
    padding-right: 1em;
    padding-left: 1em;
    align-items: center;
    color: #CDCDCD;
    font-size: 105%;
    justify-content: left;
    text-decoration: none;
    background-color: #5F4894;
    transition: .3s;
}
#page-content .collapsible-block-unfolded-link a::before {
    content: "▼";
    padding-right: 4px;
}
#page-content .collapsible-block-folded a::before {
    content: "▶";
    padding-right: 4px;
}
#page-content .collapsible-block-link:hover, #page-content .collapsible-block-link:active {
    color: #fff;
    background-color: #4e4e4e;
}
[[/module]]

[[module CSS]]
:root {
--problem: 241, 167, 92;
}

#top-bar div.open-menu a {
border: none;
background: black;
color: white;
top: unset; /*ローカライズ*/
}

.creditRate {
float: unset !important;
margin-bottom: unset !important;
}

#header h1 a::before, #header h2::before {
text-shadow: 0px 0px 12px white;
}

#header h1 a::before {
-webkit-text-stroke: 2px black;
}

#header h2 {
margin-top: 0.9rem;
}

#main-content {
top: -0.5rem;
}

#top-bar, #top-bar a {
top: 10rem;
font-weight: bold;
color: black;
}

::selection {
background: #cf1932;
text-shadow: none;
color: white;
}

.scp-image-block .scp-image-caption {
background-color: black;
color: #ffffff;
margin-top: 10px;
}

.scp-image-block {
border: none;
}

.scp-image-block img {
border: solid 1px black;
box-sizing: border-box;
}
#page-content > hr:nth-child(2), div.paper hr:not(div.paper-content hr) {
background: transparent !important;
border-top: double 4px black;
}
[[/module]]

