content:
[[module ThemePreviewer noUi="true"]]

[[module CSS]]
:root {
    --info-circle-size: 150px;
    --info-up-bg: lightgreen;
    --info-down-bg: pink;
    --info-border-color: #b01;
}

.onlyvisible {
    padding: 0 .25em;
}
.onlyvisible .page-rate-widget-box {
    align-items: center;
    display: flex;
    flex-wrap: wrap;
    font-size: 1.2em;
    justify-content: center;
    vertical-align: middle;
}
.onlyvisible .page-rate-widget-box .rate-points {
    background: #633;
    border-radius: 5px 5px 0 0;
    box-shadow: 0 0 2px #633;
    text-align: center;
    padding: .5em;
    width: 100%;
}
.onlyvisible .page-rate-widget-box .rateup,
.onlyvisible .page-rate-widget-box .ratedown,
.onlyvisible .page-rate-widget-box .cancel {
    background-color: transparent;
    border: none;
    display: block;
    flex-grow: 1;
    font-weight: bold;
    margin: 1px 0 0;
    text-align: center;
}
.onlyvisible .page-rate-widget-box .cancel {
    width: 100%;
}
.onlyvisible .page-rate-widget-box .rateup a,
.onlyvisible .page-rate-widget-box .ratedown a,
.onlyvisible .page-rate-widget-box .cancel a {
    background: #fff;
    box-shadow: 0 0 2px #633;
    color: #633;
    display: block;
    margin: 0;
    padding: .25em;
    transition: all .25s;
}
.onlyvisible .page-rate-widget-box .cancel a {
    color: #966;
}
.onlyvisible .page-rate-widget-box .rateup a:hover {
    background: var(--info-up-bg);
    color: #fff;
}
.onlyvisible .page-rate-widget-box .ratedown a:hover {
    background: var(--info-down-bg);
    color: #fff;
}
.onlyvisible .page-rate-widget-box .cancel a:hover {
    background: #966;
    color: #fff;
}

.onlyvisible .page-rate-widget-box .rateup a::before {
    content: '提案に賛成 (';
}
.onlyvisible .page-rate-widget-box .ratedown a::before {
    content: '提案に反対 (';
}
.onlyvisible .page-rate-widget-box .cancel a::before {
    content: '投票をキャンセル (';
}
.onlyvisible .page-rate-widget-box .rateup a::after,
.onlyvisible .page-rate-widget-box .ratedown a::after,
.onlyvisible .page-rate-widget-box .cancel a::after {
    content: ')';
}

.list-pages-box .rate_t3 {
    display: inline-block;
    margin: 1em 0 0;
}
.list-pages-box .rate_t3 .wrap {
    border-bottom: 1px solid var(--info-border-color);
    display: inline-block;
    width: var(--info-circle-size);
    height: calc(var(--info-circle-size) / 2);
    overflow: hidden;
}
.list-pages-box .rate_t3 .cir {
    width: var(--info-circle-size);
    height: var(--info-circle-size);
    margin: 0;
    padding: 0;
    transform: rotate(-90deg);
}
.list-pages-box .rate_t3 .rad {
    width: calc(100% - 2px);
    height: 0;
    padding: calc(50% - 1px) 0 0;
    margin: 0;
    border: solid var(--info-border-color);
}
.list-pages-box .rate_t3 .rad_def {
    background: gray;
    border-radius: 50%;
    width: calc(100% - 2px);
    height: 0;
    padding: calc(100% - 2px) 0 0;
    margin: 0;
    border: 1px solid var(--info-border-color);
}
.list-pages-box .rate_t3 .uv {
    border-radius: 100rem 100rem 0 0;
    background: var(--info-up-bg);
    border-width: 1px 1px 0;
}
.list-pages-box .rate_t3 .dv {
    border-radius: 0 0 100rem 100rem;
    background: var(--info-down-bg);
    border-width: 0 1px 1px;
}
[[/module]]


[[div class="onlyvisible"]]
[[module Rate]]

[[module ListPages name="="]]
[[=]]
[[table class="rate_t3"]]
[[row]]
[[cell colspan="2" style="display:[[#ifexpr %%rating_votes%% == 0 | none | table-cell ]];"]]
[[div class="wrap"]]
[[div class="cir" style="transform: rotate([[#expr (%%rating_votes%%-%%rating%%)/2/%%rating_votes%%*(-180)]]deg)"]]
[[div class="rad uv"]]
[[/div]]
[[div class="rad dv"]]
[[/div]]
[[/div]]
[[/div]]
[[/cell]]
[[cell colspan="2" style="display:[[#ifexpr %%rating_votes%% == 0 | table-cell | none ]];"]]
[[div class="wrap"]]
[[div class="cir"]]
[[div class="rad_def"]]
[[/div]]
[[/div]]
[[/div]]
[[/cell]]
[[/row]]

[[row style="display:[[#ifexpr %%rating_votes%% == 0 | none | table-row ]];"]]
[[cell style="text-align: left;"]]
提案に賛成
[[#expr 100 - (%%rating_votes%%-%%rating%%)/2/%%rating_votes%%*100*100%10000/100]]%
([[#expr (%%rating%%+%%rating_votes%%)/2]]票)
[[/cell]]
[[cell style="text-align: right;"]]
提案に反対
[[#expr (%%rating_votes%%-%%rating%%)/2/%%rating_votes%%*100*100%10000/100]]%
([[#expr (%%rating_votes%%-%%rating%%)/2]]票)
[[/cell]]
[[/row]]

[[row style="display:[[#ifexpr %%rating_votes%% == 0 | table-row | none ]];"]]
[[cell style="text-align: left;"]]
提案に賛成
-%
(0票)
[[/cell]]
[[cell style="text-align: right;"]]
提案に反対
-%
(0票)
[[/cell]]
[[/row]]

[[/table]]
[[/=]]
[[/module]]
[[/div]]

[[div class="hiddenDisclaimer"]]
提案の内容は、[*http://scp-jp.wikidot.com/forum/t-13775990/100kb こちら]を参照してください。
[[/div]]


