[[module css]]

@import url('https://fonts.googleapis.com/css?family=PT+Mono&display=swap');

/*
    Powered on 2019/05/09 06:09:42
    [2019 Wikidot Theme]
    Created by The Great Hippo
    CC BY-SA 3.0
*/

/* FONTS AND COLORS */

:root {
    --bg-light: #262626;
    --bg: #1a1a1a;
    --bg-dark: #000000;

    --fg-light: #ffffff;
    --fg:  #f2f2f2;
    --fg-dark: #e6e6e6;

    --fg-accent-light: #ffffcc;
    --fg-accent: #ffff99;
    --fg-accent-dark: #ffff80;

    --fg-accent-2: #ffffff;

}

body {
    background: var(--bg);
    color: var(--fg);
    font-family: 'PT Mono', monospace;
}

a, a:visited {
    color: var(--fg-accent-dark);
}

h1, h2, h3, h4, h5, h6, #page-title {
    color: var(--fg-light);
    font-family: 'PT Mono', monospace;
}

ins, del {
    color: black;
}

/* HEADER */

div#container-wrap {
    background: url(http://scp-wiki.wdfiles.com/local--files/component%3Acreepypasta-theme/gradient.png) top left repeat-x;
}

div#header {
    background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/indonootoko/%E3%83%91%E3%83%A9%E3%82%A6%E3%82%A9%E3%83%83%E3%83%81.png) 10px 40px no-repeat;
}

div#header h1 a span {
    /* hiding header text */
    font-size: 0px;
    color: transparent;
}

div#header h1 a:before {
    content: " ";
    color: rgb(24, 24, 24);
}

div#header h2 span {
    font-size:0px;
    padding: 4px;
}

div#header h2:after {
    content: " ";
    font-weight: bold;
    color: rgb(255, 224, 135);
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

/* SIDE-BAR */

div#side-bar a,
div#side-bar a:visited {
    color: var(--fg-dark);
}

div#side-bar div.side-block {
    background: rgb(24, 24, 24) !important;
    border: 1px solid #ffe087;
    box-shadow: 0 2px 6px #404040;
}

div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(255, 224, 135);
    border-color: rgb(255, 224, 135);
}

/* Recolors the image bullet points in the side-bar */
.side-block .menu-item > .image {
    filter: hue-rotate(230deg) invert(100%);
}

/* Recolors the Translation Module */
div.scpnet-interwiki-wrapper {
    filter: hue-rotate(230deg) invert(100%);
}

/* Recolors the panel that opens on mobile with the side-bar */
@media (max-width: 767px) {
    #side-bar{
        background-color: var(--bg);
    }
}

/* Recolors the drop down from the arrow next to username in the header */
#login-status ul a {
    color: var(--fg);
    background: var(--bg);
    border-color: var(--fg);
}

#login-status ul a:hover {
    color: var(--fg-dark);
}

/* Makes the code readable */
.hl-identifier, .hl-code, .hl-brackets {
    color: var(--fg);
}

/* Top-Bar */
#top-bar a {
    color: var(--fg-dark);
}

#top-bar ul li ul {
    border-color: var(--fg-light);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
   border-top-color: var(-fg-light);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background-color: var(--bg-light);
    color: var(--fg-dark);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--bg-dark); /* top bar hover background color */
    color: var(--fg-dark);
}

div.yui-navset div.yui-content {
    background: rgb(24, 24, 24);
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: rgb(24, 24, 24);
    color: rgb(255, 237, 185);
    border: solid 1px #ffffff;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: rgb(40, 40, 40);
    color: rgb(255, 237, 185);
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(50, 50, 50);
    color: var(--fg);
    border: solid 2px #ffff99;
}

/* rating module */

div.page-rate-widget-box .rate-points {
    background-color: var(--bg-dark);
}

div.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    background-color: var(--bg-dark);
    color: white;
    border: 0;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
    background-color: var(--bg-dark);
    border: 0;
    border-left: 1px solid var(--fg-accent-light);
}

div.page-rate-widget-box .rate-points,
.page-rate-widget-box .cancel a {
    border: 0;

}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background-color: var(--bg-dark);
    color: white;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover {
    background-color: var(--bg-dark);
    color: white;
}

/* footnote hovers */

.hovertip {
    width: 20em;
    padding: 0.5em;

    border: 1px solid var(--fg-accent-light);
    border-radius: 0.5em;

    background-color: #000000ee !important;
    box-shadow: 0 0 6px 1px #000000aa;
    z-index: 100;
}

.hovertip .f-heading {
    color: var(--fg-accent-light);
}

/* inputs */

input.text, input.button,
#search-top-box-input,
#search-top-box-form input[type=submit],
div.buttons input, input.button, button, file, a.button {
    background: transparent;
    color: var(--fg);
    border: 1px solid var(--fg-accent-2);
    border-radius: 0;
    font-size: 1em;
    outline: 0;
}

div.buttons input:hover, div.buttons input:focus,
input.button:hover, input.button:focus,
button:hover, button:focus,
file:hover, file:focus,
a.button:hover, a.button:focus {
    border-color: var(--fg-accent-2);
    background: transparent;
    color: var(--fg);
}

input.text:hover, input.button:hover,
input.text:focus, input.button:focus,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    color: var(--fg);
    background: transparent;
    border: 1px solid var(--fg-accent-2);
    outline: 0;
}

/* EDITING WINDOWS */
.owindow {
    color: #000;
}

.owindow h1, .owindow h2, .owindow h3, .owindow h4, .owindow h5, .owindow h6 {
    color: #000;
}

.owindow a {
    color: #272727;
}

table.page-files .highlight {
    background-color: #272727;
}

input.text {
    border: 1px dashed #000;
    color: #fff;
}

input.text:focus {
    border: 1px dashed #000;
    color: #fff;
}

input.text:hover {
    border: 1px dashed #000;
    color: #fff;
}

div#lock-info {
    background-color: var(--bg-light);
}

/* BLOCKQUOTES */

blockquote {
    background:  var(--bg-dark);
    border-width: 2px;
    border-style: solid;
    border-color: var(--bg-light);
}

/* IMAGES */

#page-content .scp-image-block {
    border-color: var(--fg-accent);
    box-shadow: none;
}
#page-content .scp-image-block .scp-image-caption {
    border-color: var(--fg-accent);
    background-color: var(--bg-dark);
}

table.wiki-content-table th {
    background-color: var(--bg-dark);
}

hr {
    background-color: var(--fg);
}

.code {
    border: 1px dashed var(--fg);
    background-color: transparent;

    font-family: "Courier", "Courier New", "Roboto Mono", monospace;
    font-size: 1em;
}

/* INFO MODULE */

#page-content .rate-box-with-credit-button {
    background-color: var(--bg-dark);
    border: 1px solid var(--bg-dark);
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: var(--fg-accent-light);
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button div.page-rate-widget-box .rate-points {
    border-left: 0;
}

#page-content .modalbox {
    background: linear-gradient(var(--bg) 51px,var(--bg-light) 51px,var(--bg-light));
    color: var(--fg);
    box-shadow: 0 2px 6px #404040;
}

.close-credits,
.credit-back {
    filter: hue-rotate(230deg) invert(100%) brightness(150%);
}

/* MOBILE CODE */
@media (max-width: 767px) {
    div#header {
        background-size: 22em;
        background-position: 1em;
    }

    .open-menu a {
        border: 0.2em solid var(--bg-light) !important;
        background-color: #787878 !important;
        color: var(--bg-light) !important;
  }
}

