[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Cinzel');
@import url('https://fonts.googleapis.com/css?family=Anton');
@import url('https://fonts.googleapis.com/css?family=Pinyon+Script');
#page-title { display: none; }

/*
    The Anton font was created by Vernon Adams and is licensed under the Open Font License
    The Cinzel font was created by Natanael Gama and is licensed under the Open Font License
    The Pinyon Script font was created by Nicole Fally and is licensed under the Open Font License
*/

#header h1 a, #header h2 span {
    font-family: 'Inter';
    font-weight: bold;
}

@media not all and (max-width: 767px) {
#main-content {
    max-width: 47.25rem;
}
}

:root {
    --light-accent: #d7003a;
    --dark-accent: #B30030;
    --hyperlink: #d7003a;
    --hyperlink-visited: #B30030;
}

div#container-wrap {
    background: url(https://scpdsandbox.wdfiles.com/local--files/niewidzialna-reka-wolnego-rynku/ar_bg.png) top left repeat-x;
}

#main-content {
    border-left: none;
}

#header {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/drzewa-wolnosci/joicl.png);
}

@media (min-width: 980px) {
    #header {
        background-position: 1.8em 3.3em;
    }
}

#header::before {
    display: none;
}

#header h1 a::before {
    content: "JOICL";
}

#header h2 span::before {
    content: "Creating New Life";
}

#page-title {
  display: none;
}

@import url('https://fonts.googleapis.com/css?family=Cinzel');
@import url('https://fonts.googleapis.com/css?family=Anton');
@import url('https://fonts.googleapis.com/css?family=Pinyon+Script');
@import url('https://fonts.googleapis.com/css?family=Lato:400,900|PT+Mono|Poppins:600,800');
@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@900&family=Raleway:wght@700&family=Roboto:wght@900&display=swap');

/*
    The Anton font was created by Vernon Adams and is licensed under the Open Font License
    The Cinzel font was created by Natanael Gama and is licensed under the Open Font License
    The Pinyon Script font was created by Nicole Fally and is licensed under the Open Font License
*/

.email {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/project-scarlet-dawn/01.png);
    background-position: top 7.5px right 7.5px;
    background-size: 70px;
    background-repeat: no-repeat;
    border: solid 3px #B30030;
    padding: 1px 15px;
    margin: auto;
    margin-top: 10px;
    margin-bottom: 10px;
    width: 85%;
}

.pentagram {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/project-scarlet-dawn/ASASAS.png);
    border-color: #0a0037;
    --hyperlink: #404d7e;
    --hyperlink-visited: #0a0037;
}

.email hr {
    border: 0;
    border-top: 2px solid #B30030;
    margin: 1em;
    padding: 0;
    display: block;
    height: 0;
}

.pentagram hr {
    border-color: #0a0037;
}
table {
    --border-colour: #B30030;
}

table.wiki-content-table th {
    background-color: #d7003a;
    color: #ffffff;
}

/* code shamelessly stolen from https://scp-wiki.wikidot.com/toesplosion2020 */
.document {
display: inline-block;
border-radius: 10px;
border:solid 1px #ccc;
background:#f0f0f0;
float:top; width:96.5%;
padding: 10px;
box-shadow: 0 2px 4px rgba(68, 68, 68, .5);
}

.click{
cursor: pointer;
}

.ping,
.fakedl{
color: #39e;
cursor: pointer;
font-weight: bold;
}

.edit{
color: #666;
font-size: 75%;
}

.edit:hover{
cursor: default;
title: "test";
}

.ping:hover{
text-decoration: underline;
}

.member{
color: #f33;
}

.member0{
color: #fff;
}

.member2{
color: #1f1;
}

.member3{
color: #838;
}

.member4{
color: #fc2;
}

.member5{
color: #0c9;
}

.member6{
color: #c04;
}

.member7{
color: #d60;
}

.member:hover,
.member0:hover,
.member2:hover,
.member3:hover,
.member4:hover,
.member5:hover,
.member6:hover,
.member7:hover,
.fakedl:active{
text-decoration: underline;
cursor: pointer;
}

blockquote {
color: #eee;
background-color: #223 !important;
border: solid 1px #444;
}

span.member:active,
span.member0:active,
span.member2:active,
span.member3:active,
span.member4:active,
span.member5:active
span.member6:active
span.member7:active
span.ping:active,
span.click:active{
background-color: #99f;
border-radius: 4px;
}

span.link a{
color: #39e;
}

.newmes{
color: #d00;
}

.red-hr > hr{
background-color: #d00;
bottom: 0.2em;
}
.president {
    border: solid 2.5px #b30030;
    outline: solid 2.5px #0a0037;
    outline-offset: 2px;
    padding: 0.95em 15px;
    margin: auto;
    width: 85%;
    --border-colour: #B30030;
    text-align: justify;
}
[[/module]]

