[[module CSS]]
body {
    background: #0D0D0D;
}

div#container-wrap {
    background-image: url("https://scpsandboxcn.wikidot.com/local--files/douglasliu-2/psychic-month-header.png");
    background-color: #0D0D0D;
    background-position: top;
    background-repeat: repeat-x;
    background-size: auto;
}

#content-wrap {
    margin: 2em 0 0 0;
    max-width: 100%;
}

#main-content {
    width: 90%;
    max-width: 910px;
    margin: 0 auto;
}

#page-title {
    display: none;
}

#page-content {
    color: white;
}

#page-content div.page-rate-widget-box {
    margin: 32px 0 48px 0;
}

#page-content div.page-rate-widget-box .rate-points {
    background: #9C4949 !important;
    border-color: #9C4949;
}

#page-content div.page-rate-widget-box .rateup,
#page-content div.page-rate-widget-box .ratedown {
    border-top-color: #9C4949;
    border-bottom-color: #9C4949;
}

#page-content div.page-rate-widget-box .rateup a,
#page-content div.page-rate-widget-box .ratedown a {
    color: #9C4949;
}

#page-content div.page-rate-widget-box .rateup a:hover,
#page-content div.page-rate-widget-box .ratedown a:hover {
    background: #9C4949;
    color: #C78F8F;
}

#page-content div.page-rate-widget-box .cancel {
    background: #9C4949;
    border-color: #9C4949;
}

#page-content div.page-rate-widget-box .cancel a {
    color: #C78F8F;
}

#page-content div.page-rate-widget-box .cancel a:hover {
    background: #9C4949;
    color: white;
}

#page-content hr {
    margin: 0;
    width: 100%;
    height: 1px;
    background: white;
    background: linear-gradient(90deg, transparent 0%, white 50%, transparent 100%);
}

#page-content div.psychic-month-title-box {
    padding: 16px 0;
    text-align: center;
}

#page-content div.psychic-month-title-box-wrapper {
    margin: 0 auto;
    width: 100%;
    max-width: 44.5rem;
}

#page-content div.psychic-month-title-box h1 {
    font-size: 300%;
    color: #9C4949;
    margin: 8px;
}

#page-content div.psychic-month-title-box h2 {
    font-size: 125%;
}

#page-content .psychic-month-links-box {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
}

#page-content .psychic-month-link {
    width: 150px;
    height: 275px;
    margin: 16px;
    flex-shrink: 0;
}

#page-content .psychic-month-link p {
    margin: 0;
}

#page-content .psychic-month-link .psychic-month-img-link {
    display: inline-block;
    width: 150px;
    height: 200px;
    background-color: transparent;
    background-size: contain;
    font-size: 0;
    opacity: 0.4;
}

#page-content .psychic-month-link .psychic-month-img-link.submitted {
    opacity: 1;
}

#page-content .psychic-month-link .psychic-month-img-link:hover {
    filter: invert(1);
}

#page-content .psychic-month-link-text {
    margin: 0.5em 0;
    text-align: center;
}

#page-content .psychic-month-link-text a {
    color: white !important;
}

span.psychic-month-color-text {
    color: #9C4949;
    font-weight: bold;
}

#page-content div.psychic-month-text-box {
    margin: 0 auto;
    width: 100%;
    max-width: 44.5rem;
}

#page-content div.psychic-month-text-box a,
#page-content div.psychic-month-text-box a:hover,
#page-content div.psychic-month-text-box a:visited {
    color: #9C4949;
}

div.page-tags a {
    color: #9C4949;
}

div#page-options-container {
    color: #555555;
}

div#page-options-container a,
div#page-options-container a:hover,
div#page-options-container a:visited {
    color: #9C4949;
}

div#license-area {
    color: #555555;
}

div#license-area a,
div#license-area a:hover,
div#license-area a:visited {
    color: #9C4949;
}

#page-content div.psychic-month-text-box h2 {
    font-size: 200%;
    font-weight: bold;
    color: #9C4949;
}

#page-content div.psychic-month-text-box h3 {
    font-size: 125%;
    font-weight: normal;
    color: white;
}

#page-content div.psychic-month-blockquote {
    margin: 1.41em 0;
    padding: 0 1.41em;
    border: 1px solid #404040;
    background: #1A1A1A;
}

#page-content div.psychic-month-blockquote p {
    line-height: 200%;
}

#page-content div.psychic-month-blockquote hr {
    opacity: 30%;
}

#page-content span.psychic-month-achivement-title {
    font-size: 125%;
    font-weight: bold;
    color: #9C4949;
}

div.footnotes-footer {
    display: none;
}

div.hovertip {
    background: #1A1A1A !important;
    border: 1px solid #404040 !important;
    color: white;
}

div.hovertip a,
div.hovertip a:hover,
div.hovertip a:visited {
    color: #9C4949;
}

div.hovertip div.f-footer {
    color: #555555;
}

@keyframes blinker {
    50% {
        opacity: 0;
    }
}

#page-content div.psychic-month-blink {
    text-align: center;
    font-size: 150%;
    font-weight: bold;
    opacity: 1;
    animation: blinker 1s linear infinite;
}

#page-content span.psychic-white-link a,
#page-content span.psychic-white-link a:hover,
#page-content span.psychic-white-link a:visited {
    color: white
}
[[/module]]

