display: none;
}

#u-display:target{
display: block;
}

/*文字を表示する平行四辺形のボックス*/
.parallelogram{
position: relative;
top: 3em;
left: 3em;
width: calc(16em + 2em);
height: 2em;
background: white;
transform: skewx(-30deg);
transform-origin: left bottom;
border: solid;
z-index:21;
animation:slide 0.79s calc(0s + 3.5s) both;
}



/*平行四辺形の動き*/
@keyframes slide{
0%{
left:-22em;
}79%{
left:3.5em;
}81%{
left:3.5em;
}100%{
left:3em;
}
}



/*文字がひずまないための見えない平行四辺形*/
.repara{
position:absolute;
top:-0.6em;
right:0em;
transform:skewx(30deg);
width: 16em;
text-align: center;
animation:mttk 0.5s linear infinite alternate;
}



/*文字を点滅させる*/
@keyframes mttk{
0%{
opacity:1;
}30%{
opacity:1;
}85%{
opacity:0;
}100%{
opacity:0;
}
}



/*シンボルの外側*/
.circle {
width: 5.5em;
height: 5.5em;
position: absolute;
top:1.25em;
left:1.25em;
z-index:23;
animation:cycle 1s ease-out calc(0s + 3.5s) backwards, cycle2 5s linear calc(1s + 3.5s) infinite both;
/*transform-origin: 50.5% 50.5%;*/
background: url(http://canon-head.wdfiles.com/local--files/divasobi/outer-logo);
background-size: 100% auto;
background-position: 50% 50%;
}



/*シンボルの内側*/
.circle2 {
width: 5.5em;
height: 5.5em;
position: absolute;
top:1.25em;
left:1.25em;
z-index:23;
animation:cycleb 1s ease-out calc(0s + 3.5s) backwards, cycle2 5s linear calc(1s + 3.5s) reverse infinite both;
/*transform-origin: 50.5% 50.5%;*/
background: url(http://canon-head.wdfiles.com/local--files/divasobi/inner-logo);
background-size: 100% auto;
background-position: 50% 50%;
}



/*回転動作（外側出現時/内側出現時/両側基本状態）*/
@keyframes cycle{
0%{
transform:rotate(0);
opacity:0;
}100%{
transform:rotate(360deg);
opacity:1;
}
}

@keyframes cycleb{
0%{
transform:rotate(0);
opacity:0;
}100%{
transform:rotate(-360deg);
opacity:1;
}
}

@keyframes cycle2{
0%{
transform:rotate(0);
}100%{
transform:rotate(360deg);
}
}

