#page-content .anima {
    position: fixed;
    height: auto;
    width:100%;
    max-width: 900px;
    left: 50%;
    transform: translateX(-50%);
    z-index: 10;
    animation-name: koukoku;
    animation-duration: 7.5s;
    animation-timing-function: linear;
    animation-delay: 1s;
    animation-direction: normal;
    animation-fill-mode: both;
    animation-play-state: running;
}
.anima br {
    display:none;
}
.anima img {
    width:100%;
}
.collapsible-block-unfolded-link p {
    margin-block-start: 0em !important;
    margin-block-end: 0em !Important;
}

.collapsible-block-unfolded-link p {
    line-height: 100%;
}

.anime * {
  box-sizing: border-box;
}
@keyframes koukoku {
0% {
    opacity: 0;
    bottom:100%;
}
7% {
    opacity: 0;
}
76% {
    opacity: 1;
}
84%,100% {
    opacity: 1;
    bottom: 0%;
}
}
.batsu::after  {
  content: '✕';
  font-weight:bold;
}
[[/module]]
