/* Warning-Box by ukwhatn */
.warning-box {
    max-width: 100%;
    margin: 2em 0;
    border: solid 2px #ea0032;
    background-color: white;
    text-align: center;
}

.warning-box .heading {
    width: 100%;
    padding: 5px;
    margin: 0;
    background: #ea0032;
    color: white;
    font-size: 1.2rem;
    line-height: 1.2rem;
    font-weight: 700;
    box-sizing: border-box;
}

.warning-box.green {
    border: solid 2px seagreen;
}

.warning-box.green .heading {
    background: seagreen;
    color: white;
}

.warning-box.green a{
    color: darkgreen;
}

.warning-box.blue {
    border: solid 2px midnightblue;
}

.warning-box.blue .heading {
    background: midnightblue;
    color: white;
}

.warning-box blockquote {
    max-width: 300px;
    padding: 0 10%;
    margin: 1em auto;
    text-align: left;
}


.warning-box .inner {
    padding: 5px 10px;
}
