border-radius: 50px 50px 50px 50px;
}
.first .second{
position: relative;
width: 90%;
height: 90%;
margin: auto;
margin-top: 5%;
border-style: dashed;
border-width: 0px;
border-radius: 30px 30px 30px 30px;
padding: 10px;
font-family: 'Aclonica';
color: rgb(75,65,50);
text-shadow:
   -1px -1px 0 rgb(200,175,150),  
    1px -1px 0 rgb(200,175,150),
    -1px 1px 0 rgb(200,175,150),
     1px 1px 0 rgb(200,175,150);
font-size: 17px;font-weight: 700;
text-align: center;
}
.third{
width: 600px;
margin: auto;
border-style: dashed;
border-width: 1px;
border-radius: 30px 30px 30px 30px;
padding: 10px;
}
