/* == COMMON == */

 :root {
    --header-title: "The Sharkest Timeline";
    --header-subtitle: "SEARCH, PUNCH, CONQUER";
    --link-txt-color: hsl(191, 100%, 25%);

    --abyssal: hsl(221, 100%, 25%);
    --terrestrial: hsl(101, 100%, 25%);
    --non-abyssal: hsl(181, 50%, 25%);
    --celestial: hsl(201, 100%, 50%);
}


 div#extra-div-1 {
    background-size: 150px;
}

 #header h1 {
    font-variant: small-caps;
}


/* == FORMATTING | [GENERAL] == */

 .meta-title {
    padding: 0;

    font-family: var(--head-font), sans-serif;
    font-variant: small-caps;
}

 #page-content :is(h1, h2, h3, h4, h5, h6) {
    font-family: var(--ui-font), sans-serif;
    font-variant: small-caps;
}


 #page-content :is(h1, h2, h3) {
    display: flex;
    justify-content: center;

    line-height: 1.2;
    text-align: center;
    text-underline-offset: 25%;
}

 li, p {
    line-height: 1.5 !important;
}

 ol li,
 ul li {
    margin: 1rem 0;
}

/* == FORMATTING | [SPECIAL] == */

 .abyssal :is(h1, h2, h3) {
    color: var(--abyssal);
}

 .terrestrial :is(h1, h2, h3) {
    color: var(--terrestrial);
}

 .non-abyssal :is(h1, h2, h3) {
    color: var(--non-abyssal);
}

 .col_box, .spec_col_box { /* = COLORFUL BOX = */
    --hex: transparent;
    --hex2: transparent;
    position: relative;
    display: inline-block;
    width: 1.2rem;
    height: 1.2rem;
    margin-bottom: .2rem;
    vertical-align: middle;

    background: var(--hex);
    border: medium solid var(--hex2);

    font-size: 0;
}

 .spec_col_box { /* = COLORFUL BOX ADD-ON = */
    width: calc(1.2rem + 3px);
    height: calc(1.2rem + 3px);
}

 .spec_col_box:before { /* = COLORFUL BOX ADD-ON = */
    position: absolute;
    inset: 0;
    padding: 3px;
    content: "";

    background:
       repeating-conic-gradient(#c7b187 0 25%, #815eba 0 50%)
     0 0/12px 12px round;
    -webkit-mask:
       linear-gradient(#000 0 0) content-box,
       linear-gradient(#000 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
}

/* == DIV BLOCKS == */

 div a {
    font-weight: bold;
}

 @media (max-width: 575.9px) {
     .modal, .jotting, .paper, .notation {
     margin: 1rem 0;
    }
}

/* == TABLES == */

 .special table {
    display: block !important;
    max-height: 85vh;
    overflow: auto;
    counter-reset: line-number;
}

 .special table a {
    font-weight: bold;
}

 .special th {
    text-align: center;
}

 .special td:first-child:before {
    content: counter(line-number) ".";
    counter-increment: line-number;
    padding-right: .3rem;
}

 .special td:nth-child(3) {
    min-width: 25%;

    line-height: 1.5;
}

 .special td:nth-child(4) {
    min-width: 20%;

    font: 1rem monospace;
    text-align: right;
}

/* == YUI TABS == */

 .yui-navset .yui-nav a {
    font-size: 1.25rem;
    font-weight: 600;
    font-family: var(--ui-font), sans-serif;
}
