[[/div]]

[[module CSS]]
.hidden-disclaimer{display:none;}
.draft-wrap h1 {
    text-align: center;
}
.draft-wrap h1 + a + div {
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.29);
}
.draft-wrap.include-true h1,
.draft-wrap.include-true blockquote,
.draft-wrap.include-true div {
    display: none;
}

.draft-wrap.include-true h1 + a + div.true {
    display: block;
}

.anc-wrap {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    border-bottom: 1px dotted #aaa;
    padding: 1em 0;
    border: dotted #aaa;
    border-width: 2px 0;
    margin-top: 1em;
}
.anc-wrap a {
    display: inline-block;
    width: 50px;
    text-align: center;
    padding: 0.5em;
    text-decoration: none;
    background: #f7f7f7;
    border: outset 2px buttonface;
    border-left: solid 6px #ff7c5c;
    color: #ff7c5c;
    font-weight: bold;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.29);
}
.anc-wrap a:active  {
    box-shadow: inset 0 0 2px rgba(128, 128, 128, 0.1);
    transform: translateY(2px);
}
.textchange div.sitemember:nth-child(2) {
    display:none;
}
.res-box .collapsible-block-unfolded-link {
    margin-top: 1.5em;
}
.res-box .collapsible-block-unfolded-link .collapsible-block-link {
    font-size: 150%;
    color: #901;
}
.reserve-tool-info {
    opacity: 1;
    animation: info 4s ease 3s forwards;
}
@keyframes info {
80% {
    opacity: 0.9;
}
99% {
    opacity: 0;
}
100% {
    visibility: hidden;
    opacity: 0;
}
}

.footnotes-footer{
display:none;
}
[[/module]]
