[[module css]]
/* --- fonts --- */
@import url("https://fonts.googleapis.com/css2?family=Special+Elite:ital,wght@0,400;0,700;1,400&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Orbitron:wght@400..900&display=swap");

#page-title {display: none;}

#login-status, #login-status a {
    color: white;
}

.licensebox {
  color: #ff0000;
}

:root {
    --header-title: "";
    --header-subtitle: "";
    --header-font: "Orbitron";
    --link-txt-color: rgb(var(--accent)); 
    --logo-img: url("http://scp-sandbox-3.wikidot.com/local--files/collab:red-queen-s-race/RQRTOPPER-removebg-preview.png");
    --header-txt-color: #d61c0f;
    --misc-txt-color: #d61c0f;
    --logo-opacity: 70%;
    --link-txt-color: #d61c0f;
} 

body {
  background-image: linear-gradient(to bottom, #660000, #000000 120px);
  color: #F5F5F5;
}

div#extra-div-1 {
    height: 160px;
    width: 100%;
    position: absolute;
    background: var(--logo-img) 10px 30px no-repeat;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: 50% 50%;
    z-index: -1;
    opacity: var(--logo-opacity);
}

.center {
  font-family: monospace;
  text-align: center;
  font-size: 15.5px;
}

.centerred {
  font-family: monospace;
  text-align: center;
  color: #ff0000;
  font-size: 15.5px;
}


.lekton {
  font-family: "Special Elite", monospace;
  text-align: center;
  display: block;
  margin-top: 1.5em;
  margin-bottom: 1.5em;
  color: #ff0000;
  font-size: 17.5px;
}

.lekton2 {
  font-family: "Special Elite", monospace;
  text-align: center;
  display: block;
  margin-top: 1.5em;
  margin-bottom: 1.5em;
  color: #ff0000;
  font-size: 22.5px;
}

.lekton3 {
  font-family: "Special Elite", monospace;
  text-align: center;
  display: block;
  margin-top: 1.5em;
  margin-bottom: 1.5em;
  color: #ff0000;
  font-size: 22.5px;
  font-style: italic;
}

.orbitron {
  font-family: "Orbitron", sans-serif;
  font-size: 2.5em;
  font-weight: bold;
  text-align: center;
  display: block;
  margin-bottom: 1em;
}

.orbitron2 {
  font-family: "Orbitron", sans-serif;
  font-size: 2.5em;
  font-weight: bold;
  text-align: center;
  display: block;
  margin-top: 1em;
  margin-bottom: 1em;
}

.orbitron3 {
  font-family: "Orbitron", sans-serif;
  font-size: 2.5em;
  font-weight: bold;
  text-align: center;
  display: block;
  margin-top: -0.5em;
  margin-bottom: 1em;
}

.hovertip {
  color: #000000;
}

.owindow {
  color: #000000;
}

#toc {
  background-color: #000000;
}

#header h2::before {
    text-transform: capitalize;
    font-style: italic;
}

.notation {
    border-width: 4px;
    width: 75%;
    margin: auto;
    border-color: rgb(var(--accent));
    text-align: justify;
}

.separation {
    margin: 1ex 15%;
    padding: 0;
    border-top: 0.2rem solid #275495;
    display: block;
}

.al-fine, .vanguard, .elaimal, .omalley, .textbook, .schemer{
    width: 85%;
    margin: auto;
    padding: 15px;
    box-shadow: 0 1px 3px rgba(0,0,0,.2);
    color: #000000;
}

.dictation {
   font-family: Courier New, monospace;
   color: #F5F5F5;
}

.schemer {
   border: solid 2.5px #ff7700;
   color: #ff7700;
}

.textbook {
    border: solid 2.5px #fff0ba;
    background-color:#fff0ba;
    color: #000000;
    font-family: Times New Roman;
}

.elaimal {
    border: solid 2.5px #39FF14;
    color: #39FF14;
    font-family: monospace;
}

.omalley {
   border: solid 2.5px #6495ED;
   color: #6495ED;
}

.president {
    font-family: monospace;
    color: #04c21a;
    font-size: 17px;
}

.al-fine {
    border: solid 4px #275495;
    background: #F2F7FB url('https://scp-wiki.wdfiles.com/local--files/theme%3Agoc/goc-fade.png') center no-repeat;
}

.vanguard {
    border: solid 4px #275495;
    background: #F2F7FB center no-repeat;
}

div.blockquote {
    background-color: #121212;
    border: 3px solid #444444;
}

.blockquote .image-container {
    padding: 0em;
}

.characters {
    margin-top: -1em;
    margin-bottom: 1em;
    margin-left: 15px;
    margin-right: 10px;
}

.characters-top {
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
    gap: 16px;
}

.bar {
    width: 12px;
    height: 85px;
}

.characters-info {
    flex-grow: 1;
}

.characters-info p {
    line-height: 200%;
}

.bio {
    margin-top: -20px;
}

.goc {
    --color: #5f95e6;
}

.us {
    --color: #404d7e;
}

.joicl {
    --color: #d7003a;
}

.phoenix {
    --color: #d68300;
}

.mcd {
    --color: #8c148c;
}

.van {
    --color: #4b2;
}

.bar {
    background-color: var(--color);
}


