[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Bangers&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Black+Ops+One&display=swap');

/* ---------------------------------
RED, WHITE, AND BLUE PATRIOT THEME MFER
FORKED FROM THE JUST LIBERAL PUSSY THINGS THEME BY SOCIALISTBREATH AND DIANACOMMIE
--------------------------------- */

/* -------------- HEADER -------------- */
div#container-wrap {
    background-image: none;
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: url("https://scp-wiki.wdfiles.com/local--files/red-white-and-blue-reality/header.png");
    background-size: cover;
    background-position: 20% 20%;
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: linear-gradient(to bottom,
     hsl(0, 100%, 37%),
     hsl(237, 100%, 19%));
}

div#header {
    background-image: url("https://scp-wiki.wdfiles.com/local--files/red-white-and-blue-reality/american.png");
    background-size: 8.6%;
    filter: drop-shadow(.1rem .1rem .063rem hsl(335, 100%, 8%));
}

#header h1 a {
    color: transparent;
    text-shadow: none;
}

#header h1 a {
    font-family: 'Black Ops One', cursive;
    color: hsl(237, 100%, 19%);
    text-shadow: none;
    filter: drop-shadow(.1rem .1rem .063rem hsl(4, 0%, 100%));
    text-transform: uppercase;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: "FUCK YEAAAAAHHHHH";
    color: hsl(0, 100%, 37%);
    font-family: 'Black Ops One', cursive;
    filter: drop-shadow(.1rem .1rem .063rem hsl(4, 0%, 100%));
    letter-spacing: 2px;
    padding-left: 4em;
}

/*-------------- LOG-IN INFO -------------- */
#login-status,
#login-status a {
    color: hsl(335, 0%, 100%);
}

#login-status ul a:hover {
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: hsl(335, 0%, 0%);
    border: none;
}

#account-options {
    background: hsl(237, 100%, 19%);
    border-color: hsl(0, 100%, 37%);
}

/* -------------- SEARCH-BAR -------------- */
#search-top-box-input {
     border: solid 1px #ffffff;
     color: #ffffff;
     background-color: hsl(0, 100%, 35%);
}

#search-top-box-input:hover,
#search-top-box-input:focus {
     background-color: hsl(236, 100%, 34%);
}

#search-top-box-form input[type=submit] {
     border: solid 1px #ffffff;
     color: #ffffff;
     background-color: hsl(237, 100%, 39%);
     background: linear-gradient(
       to bottom,
       hsl(237, 100%, 29%),
       hsl(237, 100%, 19%),
       hsl(237, 100%, 9%));
     box-shadow: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
     background-color: hsl(237, 100%, 60%);
     background: linear-gradient(
       to bottom,
       hsl(237, 100%, 70%),
       hsl(237, 100%, 60%),
       hsl(237, 100%, 50%));
     box-shadow: none;
}

 a,
#side-bar a:visited,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover,
a.newpage {
    color: hsl(234, 100%, 27%);
}
/* -------------- BODY -------------- */
body {
     background: hsl(218, 100%, 80%);
     color: hsl(238, 100%, 15%);
}

#page-title, h1 {
    color: hsl(238, 100%, 15%)
}

#page-title {
    border-bottom-color: hsl(238, 100%, 15%);
}

/* -------------- SIDE-BAR -------------- */
#side-bar .side-block {
    border: 1px solid hsl(0, 100%, 25%);
    box-shadow: 0 2px 6px rgba(255, 0, 0);
    color: hsl(232, 100%, 38%);
    background: hsl(232, 97%, 68%);
}

#side-bar .heading {
    color: hsl(234, 100%, 32%);
    border-bottom: solid 1px hsl(0, 100%, 25%);
}

#side-bar .side-block.media {
    background-color: hsl(232, 97%, 68%);
}

#side-bar .side-block.resources {
    background-color: hsl(232, 97%, 68%);
}

.side-block .menu-item > .image,
iframe.scpnet-interwiki-frame {
    filter: hue-rotate(340deg) brightness(130%);    
    -webkit-filter: hue-rotate(340deg) brightness(130%);
}

/* -------------- FOOTER -------------- */
#footer {
    background: hsl(240, 100%, 50%);
    color: hsl(0, 100%, 50%);
}

/* -------------- LINKS -------------- */
a,
#side-bar a:visited,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover,
a.newpage {
    color: hsl(234, 100%, 27%);
}

a:visited {
    color: hsl(0, 100%, 30%);
}

/* -------------- PAGE EDITING OPTIONS -------------- */
.buttons .btn {
    background-color: hsl(235, 90%, 95%);
    border-color: hsl(235, 90%, 85%);
    color: hsl(235, 100%, 14%);
}

.buttons .btn:hover {
    background-color: hsl(235, 90%, 90%);
    color: hsl(235, 100%, 14%);
}

#lock-info {
    background-color: hsl(235, 90%, 95%);
    border-color: hsl(0, 100%, 50%);
}

/* -------------- RATING MODULE -------------- */
.page-rate-widget-box .rate-points {
    background-color: hsl(237, 55%, 55%) !important;
    color: hsl(0, 90%, 95%) !important;
    border: solid 1px hsl(237, 55%, 55%);
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: hsl(0, 90%, 95%);
    border-top: solid 1px hsl(237, 55%, 55%);
    border-bottom: solid 1px hsl(0, 55%, 55%);
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: hsl(237, 55%, 55%);
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: hsl(0, 55%, 55%);
    color: hsl(237, 90%, 95%);
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: hsl(0, 55%, 55%);
    border: solid 1px hsl(237, 55%, 55%);
}
.page-rate-widget-box .cancel a {
    color: hsl(0, 90%, 95%);
}
.page-rate-widget-box .cancel a:hover {
    background: hsl(237, 90%, 95%);
    color: hsl(0, 55%, 55%);
}

/* -------------- OTHER ELEMENTS -------------- */
blockquote,
div.blockquote,
.code {
    border-color: hsl(0, 90%, 85%);
    background-color: hsl(237, 90%, 95%);
}

#page-content .wiki-content-table tr th {
     background-color: hsl(237, 55%, 55%);
     color: hsl(237, 100%, 98%);
     border: 1px solid hsl(0, 100%, 14%);
}

.scp-image-block .scp-image-caption {
    background-color: hsl(0, 90%, 95%);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: hsl(237, 90%, 75%); /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent !important; /* tab background */
    border-color: transparent;
    color: hsl(237, 100%, 14%);
}

.yui-navset .yui-nav a em {
     background-color: hsl(237, 90%, 95%);
     box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
     border-color: hsl(0, 90%, 85%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: hsl(237, 55%, 55%); /* selected tab background */
    color: hsl(0, 100%, 98%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: hsl(237, 55%, 55%);
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    background-color: hsl(0, 55%, 55%);
}

.yui-navset .yui-content {
    background-color: hsl(0, 90%, 95%); /* content background color */
}

/* -------------- MOBILE VIEW -------------- */
@media (max-width: 767px) {
    #side-bar {
          background-color: hsl(237, 65%, 65%);
     }

    #top-bar .open-menu a {
         border: 0.2em solid hsl(237, 100%, 85%);
         background-color: hsl(0, 100%, 98%);
         color: hsl(237, 100%, 85%);
    }
}

#page-content,
#page-content a:hover {
    cursor:url(https://scp-wiki.wdfiles.com/local--files/red-white-and-blue-reality/eagleemoji.png),auto;
    cursor:url(https://scp-wiki.wdfiles.com/local--files/red-white-and-blue-reality/eagleemoji.png),pointer;
}
