[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Barlow:ital,wght@0,900;1,900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Barlow+Condensed:ital,wght@0,300;1,300&display=swap');

.sign {
background-blend-mode: difference;
}

.ature, .ature a, .ature a:visited, .sign p {
  color: #ececec!important;
}


.latest {
width: fit-content;
margin: auto;
padding: 0rem 1rem;
border: 3px solid rgb(var(--bright-accent));
border-radius: 0.75rem;
margin-top: 2rem;
}

.intro {
    border-left: 3rem solid rgb(var(--bright-accent));
    padding-left: 0.75rem;
}

.continue {
    border-right: 3rem solid rgb(var(--bright-accent));
    padding-right: 0.75rem;
    text-align: right;
}

h1, h3 {
    color: rgb(var(--bright-accent));
    font-family: 'Barlow', sans-serif;
}

.timeline {
    border-left: 0.3rem solid rgb(var(--bright-accent));
    padding-left: 0.75rem;
}

span.timestamp {
  position: relative;
  font-size: 95%;
  font-family: 'barlow condensed';
  left: -3.5rem;
}

.timestamp::after {
  content: " •";
}
/*
p:has(> .timestamp) {
    height: 0px;
} 
*/
.proposal {
    display: block;
    height: 13rem;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
    mix-blend-mode: plus-lighter;
}

.proposal:visited {     color: white;}
.proposal:hover, .proposal:focus, .proposal:active, .proposal:focus-within {
    text-decoration: none!important;
}

.name {
    font-family: 'Barlow', sans-serif;
    font-size: 200%;
    font-style: italic;
}

.subtitle {
    font-family: 'Barlow Condensed', sans-serif;
    color: white;
    FONT-SIZE: 125%;
    position: relative;
    top: -0.5rem;
    left: 2rem;
    font-style: italic;
}

.sin {
    background-image: url('https://scp-jp-storage.wdfiles.com/local--files/file%3A5700026-95-3qrf/800px-Vincent_Wigglesworth.jpg');
    background-position: 35% 50%;
    background-size: 160%;
    background-color: rgb(var(--bright-accent));
    background-blend-mode: multiply;
    transition: all 200ms ease-in-out;
    color: white!important;
}

.sin:hover {
    background-color: #6e95f2;
    box-shadow: 0px 0px 9px 3px rgba(110, 149, 242,0.5);
    text-shadow: 0px 0px 10px rgba(0,0,0,0.75);
}

.gold {
    background-image: url('https://scp-jp-storage.wdfiles.com/local--files/file%3A5700026-96-aj6o/amoniram.png');
    background-position: center 35%;
    background-size: 160%;
    background-color: rgba(var(--bright-accent), 0.85);
    background-blend-mode: color-burn;
    transition: all 200ms ease-in-out;
    color: white!important;
}

.gold:hover {
    color: #fdcc0a!important;
    /* background-color: rgba(164, 119, 84, 1);*/
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(253, 204, 10, 0.45);
}

.jade { 
    background-image: url('https://scp-jp-storage.wdfiles.com/local--files/file%3A5700026-97-s3xx/JadeHeader2k.jpg');
    background-size: 120%;
    background-color: rgb(var(--bright-accent));
    background-blend-mode: luminosity;
    background-position: center;
    transition: all 200ms ease-in-out;
    color: white!important;
}

.jade:hover {
    color: #55BD43!important;
    background-color: #295522;
    box-shadow: 0px 0px 9px 3px #37782C;
}

.bone {
    background-image: url('https://scp-jp-storage.wdfiles.com/local--files/file%3A5700026-98-dr50/redtapeREDACTED.png');
    background-size: cover;
    background-position: center;
}

.mm {
background-image: url('https://scp-jp-storage.wdfiles.com/local--files/file%3A2375337-52-z0ci/manor.png');
background-size: cover;
background-color: rgb(var(--bright-accent));
background-blend-mode: color-burn;
    transition: all 200ms ease-in-out;
    color: white!important;
}

.mm:hover {
    color: rgb(var(--bright-accent))!important;
    background-color: #c19d9d;
    box-shadow: 0px 0px 9px 3px rgba(var(--bright-accent), 0.5);
    text-shadow: 0px 0px 10px black;
}
.article {
    display: block;
    /* background-image: url('https://scp-jp-storage.wdfiles.com/local--files/file%3A5700026-99-sgw8/topolight.png'); */
    text-align: center;
    width: fit-content;
    margin: auto;
    padding: 0rem 1rem;
    border-left: 5px solid rgb(var(--bright-accent));
    border-radius: 2px;
    font-family: 'barlow condensed';
    font-size: 135%;
    background-size: 450%;
    transition: all 500ms ease-in-out;
    text-transform: uppercase;
}

.article:hover {
    color: white!important;
    background-color: rgb(var(--bright-accent));
    /* background-blend-mode: multiply; */
    box-shadow: 0px 0px 9px 3px rgba(var(--bright-accent), 0.5);
    text-decoration: none!important;
    mix-blend-mode: plus-lighter;
}

.raisa { border-left-color: #5c85ff;}
.raisa { color: #5c85ff;}
.raisa:visited { color: #355fd9;}
.raisa:hover {
    background-color: #5c85ff;
    box-shadow: 0px 0px 9px 3px rgba(42, 70, 155, 0.5);
}

.starsite { border-left-color: #ffcb00;}
.starsite { color: #ffcb00;}
.starsite:visited { color: #d9ac00;}
.starsite:hover {
    color: black!important;
    background-color: #ffcb00;
    box-shadow: 0px 0px 9px 3px rgba(255, 203, 0, 0.5);
}

.attrib { color: grey; font-size: 80%; text-transform: lowercase;    transition: all 100ms ease-in-out; }
.attrib::before { content: " | "}
.article:hover > .attrib { color: black;}

#page-content a.collapsible-block-link {
  overflow-wrap: inherit;
  font-weight: bold;
  font-family: 'Barlow', sans-serif;
  font-size: 120%;
}
@media only screen and (max-width: 800px) {
   span.timestamp {
       left: 0rem;
   }

    .timestamp::after {
        display: none;
    }

    .timestamp::before {
        content: "• ";
    }

    .proposal {
        height: 7rem;
    }
}

