[[module CSS]]

.scp-image-caption { color: #000; }

/* FONTS AND COLORS */

:root {
    --bg-light: #262626;
    --bg: #1a1a1a;
    --bg-dark: #000000;

    --fg-light: #ffffff; 
    --fg:  #f2f2f2;
    --fg-dark: #e6e6e6;

    --fg-accent-light: #ffffcc;
    --fg-accent: #ffff99;
    --fg-accent-dark: #ffff80;

    --fg-accent-2: #ffffff;

}

body {
    background: var(--bg);
    color: var(--fg);
    font-family: 'PT Mono', 'IBMPlexSansKR-Light', monospace;
}

h1, h2, h3, h4, h5, h6 {
    color: var(--fg-light);
    font-family: 'PT Mono', 'UnTaza', monospace;
}

#page-title {
    color: var(--fg-light);
    font-family: 'PT Mono', 'UnTaza', monospace;
}


:root {
  --header-logo: url(http://scpkosb.wikidot.com/local--files/draft:dr-sagan-41-b2d2/kd) 0px 35px no-repeat;

  --color-bg: repeat-x top/100% url("http://scpkosb.wikidot.com/local--files/draft:dr-sagan-43-f771/rd");
}

.rate-box-with-credit-button, .page-rate-widget-box {
  --color-bg: #404141;
}

#breadcrumbs {
  display: none;
}

 #search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
     background: #FFF;
}

 h2, h3 {
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
}

 #header h1 a::before {
     color: #FFF;
     text-shadow: 0 -3px #404141, 0 3px #404141, 3px 0 #404141, -3px 0 #404141, 2px 2px #404141, 2px -2px #404141, -2px 2px #404141, -2px -2px #404141;
}
.item {
  order: 1;
  border-radius: 5px;
  background: #404141;
  font-size: 110%;
  font-weight: 700;
  text-align: center;
  width: auto;
  margin: 5px;
  padding: 0 15px 0 15px;
}

.loc {
  position: absolute;
  transform: translate(-20px, -7px);
  background: #FFF;
  border: 2px solid #404141;
  padding: 4px;
  border-radius: 15px;
  color: #404141;
  font-size: 80%;
  vertical-align: center;
  text-align: center;
}

ul ul, li ul {
  margin-left: -10px;
}

.footnotes-footer .title {
  display: none;
}

@media (max-width: 676px) {
  :root {
     --color-bg: no-repeat center/auto 100% url("http://scpkosb.wikidot.com/local--files/draft:dr-sagan-43-f771/rd");
  }
}

