/*  rate  */

.creditRate .page-rate-widget-box {
  border-radius: unset;
  box-shadow: unset;
}
.creditRate div.page-rate-widget-box .rate-points, .creditRate .page-rate-widget-box .cancel {
  background-color: #b8d4d4;
  border: #b8d4d4;
  border-radius: unset;
  color: #333;
}
.creditRate .page-rate-widget-box .rateup, .creditRate .page-rate-widget-box .ratedown {
  background-color: #b8d4d4;
  border-top: #b8d4d4;
  border-bottom: #b8d4d4;
}
.creditRate .rate-box-with-credit-button .creditButton {
  height: 18px;
  background-color: #b8d4d4;
  border: #b8d4d4;
  border-radius: unset;
  box-shadow: unset;
  margin-left: unset;
}
.creditRate .page-rate-widget-box .rateup a, .creditRate .page-rate-widget-box .ratedown a, .creditRate .page-rate-widget-box .cancel a, .creditRate .rate-box-with-credit-button .creditButton a {
  color: #333;
}
.creditRate .page-rate-widget-box .rateup:hover, .creditRate .page-rate-widget-box .ratedown:hover, .creditRate .creditButton:hover {
  background: #fbfbfb;
}
.creditRate .page-rate-widget-box .rateup a:hover, .creditRate .page-rate-widget-box .ratedown a:hover, .creditButton a:hover {
  background: #fbfbfb;
  color: #333;
}
.creditRate .page-rate-widget-box .cancel a:hover {
  border-radius: unset;
  background: #fbfbfb;
  color: #333;
}
.page-rate-widget-box {
  border-radius: unset;
  box-shadow: unset;
}
div.page-rate-widget-box .rate-points, .page-rate-widget-box .cancel {
  background-color: #b8d4d4 !important;
  border: #b8d4d4;
  border-radius: unset;
  color: #333 !important;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
  background-color: #b8d4d4;
  border-top: #b8d4d4;
  border-bottom: #b8d4d4;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a, .page-rate-widget-box .cancel a {
  color: #333 !important;
}
.page-rate-widget-box .rateup:hover, .page-rate-widget-box .ratedown:hover {
  background: #fbfbfb;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
  background: #fbfbfb;
  color: #333;
}
