[[module CSS]]

div#container-wrap {
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/2024-blank/margin2.png); /*顶栏背景图片*/
    background-repeat: repeat-x; /*顶栏背景图片平铺方式：X轴方向平铺*/
}

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: url(http://scpsandboxcn.wdfiles.com/local--files/2024-blank/LOGO2.png) 5px 30px no-repeat;
    background-size: 105px;
}

#header h1 a::before {
    content: "RKATSITELICC";
    font-size: 80%;
    font-family: 'Exo', sans-serif;
    font-weight: 800;
    text-shadow: 4px 4px 4px rgba(0,0,0,.8);
}
 @media (max-width: 479px) { 
#header{
    background-position: 0 5.5em;
    background-size: 55px 55px;
}
}

@media (min-width: 480px) and (max-width: 580px) { 
#header{
background-position: 0.5em 4.5em;
background-size: 66px 66px;
}
}
#header h1 a span{
    visibility: hidden;
}

#header h2 span {
    color: transparent;
    text-shadow: 0px 0px 0px;
}

#header h2 span::before {
    content: "哦，我的朋友，这似乎不是很cooooooooool";
    color: #ffffff;
    font-family: 'Exo', sans-serif;
    font-weight: 400;
    text-shadow: 3px 3px 3px rgba(0,0,0,.9);
}
[[/module]]
