[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Josefin+Sans:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+Gurmukhi:wght@100..900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Platypi:ital,wght@0,300..800;1,300..800&display=swap');

#page-title {
    display: none;
}
.list-pages-item > .collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    border-top: 0.125rem dashed slategrey;
    border-bottom: 0.125rem dashed slategrey;
     background-color: #cacaca45;
}
.list-pages-item > .collapsible-block .collapsible-block-content {
    text-align: left;
}
.doc { margin-bottom: -2rem;}
.bot {
    width: fit-content;
    max-width: 35%;
    background: #260000;
    border: 3px solid #bd0000;
    color: #f70000;
    border-radius: 5px;
    padding: 0rem 1rem;
    margin: 0.5rem 0rem;
    font-weight: bold;
    box-shadow: 0px 0px 15px rgba(255, 0, 0, 0.15);
    float: left;
    clear: both;
    text-shadow: 0px 0px 4px #f700008a;
}

.user {
    width: fit-content;
    max-width: 35%;
    background: #001f26;
    border: 3px solid #00a26d;
    border-radius: 5px;
    color: #00a26d;
    padding: 0rem 1rem;
    margin: 0.5rem 0rem;
    font-weight: bold;
    /* position: relative;
    left: 27.5rem; */
    box-shadow: 0px 0px 15px rgba(0, 255, 231, 0.15);
    float: right;
    clear: both;
    text-shadow: 0px 0px 3px #00f7e08a
}

.splash {
    text-shadow: 2px 2px 10px #00000052;
}

.act {
    font-family: 'Josefin Sans', sans-serif;
    color: #000;
    font-size: 5rem;
    text-align: center;
    margin-bottom: -17rem;
}
.sub {
    font-family: 'Josefin Sans', sans-serif;
    color: #000;
    font-size: 500%;
    text-align: center;
    text-shadow: 0px 0px 10px #00000080;
    margin-bottom:-4rem;
}
.num {
    font-family: "Noto Sans Gurmukhi", sans-serif;
    font-weight: 700;
}

.quote {
font-family: "Platypi", serif;
color: white;
  margin: 1rem 5rem;
  background: #1d1d1d;
  padding: 0.5rem 2rem;
  text-align: right;
  border: 4px double white;
  font-weight: 800;
  font-size: 90%;
}

.qna {
  border: 5px white outset;
  background: rgb(var(--medium-accent));
  box-shadow: 1px 1px 4px rgba(var(--dark-accent), 0.5);
  color: white;
  padding: 1rem;
  margin: 1.5rem 0.5rem;
}

.sealed {
  background-color: rgb(var(--medium-accent));
  color: white;
  margin-bottom: 0.8rem;
  padding: 3rem 0.5rem;
  background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Aredtape/topolight.png');
  background-blend-mode: overlay;
  background-size: 200%;
  border: 4px solid black;
  border-radius: 2px;
  transition: all ease-in-out 0.5s;
  background-position: center;
}
.sealed:hover {
  background-color: rgb(var(--bright-accent));
  background-size: 250%;
}

.links {
display: block;
text-align: center;
}
.links a {
    background: rgb(var(--bright-accent));
    color: white;
    padding: 3px;
    border-radius: 4px;
    font-family: monospace;
    white-space: nowrap;
}
.links a:visited {
    color: white;
}
.links a:hover {
    color:rgb(var(--bright-accent));
}



@media only screen and (max-width: 765px) {
    .user {
        float: none;
        position: relative;
        left: 45vw;
     }
     .bot {
         float: none;
     }
    .quote {
        margin: 1rem auto;
    }
}

