[[include :scpko:theme:navla-style]]

[[module CSS]]
@import url('https://scpko.wdfiles.com/local--files/korean-font/GmarketSans.css');
@import url('https://fonts.googleapis.com/css?family=Nanum+Gothic');
@font-face {
    font-family: '국립박물관문화재단클래식L';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_twelve@1.0/국립박물관문화재단클래식L.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

:root:lang(ko) {
    --logo-image: url('http://scpko.wdfiles.com/local--files/main/logo_white.png');
    --header-txt-color: 0, 0, 0;
    --bg-color: 255, 255, 255;
    --theme-color: 0, 0, 0;
    --title-font: 'GmarketSansLight', sans-serif;
    --clrd: 1;
    --alt-h1-hlt-opa: 0;
    --title-align: left;
    --header-height: 170px;
    --h1-top: 35px;
    --logo-top: 20px;
    --h1-size: 40px;
    --logo-size: 100px;
    --left: 1;
    --body-font: 'Nanum Gothic', sans-serif;
    --alt-link-opa: 0;

}

div#header-extra-div-1 {
    opacity: 1;
}
div#header-extra-div-2 {
    background: linear-gradient(rgba(var(--bg-color),0.9), rgba(var(--bg-color),0.5));
    backdrop-filter: blur(2px);
    -webkit-backdrop-filter: blur(2px);
}
div#header-extra-div-3 {
    background: rgb(255 255 255);
    outline: solid 1.5px rgb(0 0 0);
}
div#extra-div-4 {
    background: none;
}
.fancyborder {
    box-sizing: border-box;
    padding: 2vw;
    border: 2vw solid rgba(0, 0, 0, 0.5);
    border-image: url("https://wanderers-library-ko.wikidot.com/local--files/component:theme/wl_border.png") 600 round;
    border-image-width: 6;
}
#page-title,
.meta-title {
    display: none;
}
}
[[/module]]

