[[module CSS show="true"]]
@import url('http://scpko.wdfiles.com/local--files/component%3Atheme/font-bauhaus.css');
@font-face {
    font-family: 'SEBANG_Gothic_Bold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2104@1.0/SEBANG_Gothic_Bold.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

#header h1 a {
     display: block;
     margin: 0;
     padding: 80px 0 25px;
     line-height: 0px;
     max-height: 0px;
     color: #eee;
     background: transparent;
     font-family: BauhausLTDemi, 'SEBANG_Gothic_Bold', Arial, sans-serif;
     font-size: 180%;
     text-decoration: none;
     text-shadow: 3px 3px 5px #000;
     letter-spacing: 0.9px;
}

div#header h1 a:before {
    content: "Bauhaus";
}
[[/module]]

