body{
}

.poem{
text-indent: 0;
margin: auto;
margin-top: 0.5em!important;
margin-bottom:0.5em;
font-size:2.0em;
font-family: 'UnGungseo',  monospace;
}

.hover {
text-decoration: none;
    border-bottom: none;
    color:black;
}
.hover:hover {
     text-decoration: none;
    border-bottom: none;
    color:#c90926;
}

.hover span {    display: none;}
.hover:hover span {
    position: absolute;
    display: inline;
    margin: 20px 0px;
    height: auto;
    width: auto;
    background: #FFF;
    border: 1px solid #000;
    color: black;
    padding: 0.5em;
}
.hover:hover span span {
    position: relative;
    margin: auto;
    height: auto;
    width: auto;
    border: none;
    padding: 0;
}
[[/module]]

