[[module CSS]]
@font-face { font-family: "ChusaLoveBold"; src: url("https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_2205@1.0/ChusaLoveBold.woff2") format("woff2"); font-style: normal; font-weight: 700; }

#side-bar, #side-bar .side-block,
#side-bar .collapsible-block-folded {
  background: transparent;
}

#page-title, #page-content h3:first-of-type a, #side-bar {
  text-shadow: 2px 0 1px #FFF, -2px 0 1px #FFF, 0 2px 1px #FFF, 0 -2px 1px #FFF, 1px 1px 1px #FFF, -1px 1px 1px #FFF, 1px -1px 1px #FFF, -1px -1px 1px #FFF;
}

div#extra-div-3, div#extra-div-4, div#extra-div-5, div#extra-div-6 {
  position: absolute;
  background: linear-gradient(#FFAA5D, #FFAA5D 100px, #FF7A00);
  z-index: -1;
}
div#extra-div-4, div#extra-div-5, div#extra-div-6 {
  filter: brightness(0.95);
}

div#extra-div-3 {
  background: url('https://scpko.wdfiles.com/local--files/samcheonri-contest-chungnam/cs_bg.jpg') top/cover no-repeat;
  top: 150px;
  height: 1000px;
  width: 100vw;
  opacity: 0.4;
  mask-image: linear-gradient(rgb(0 0 0 / 100%), transparent);
}

div#extra-div-4 {
  background: linear-gradient(#FFDAB9, #FFAA5D 300px, #FFAA5D);
  top: 150px;
  left: 10vw;
  width: calc(40vw + var(--offset));
  height: 350px;
  clip-path: polygon(calc(50% - 30px) 0, calc(100% - 100px) 350px, 100% 350px, 100% 300px, calc(50% + 20px) 0);
}

div#extra-div-5 {
  top: 450px;
  left: calc(50vw - 100px + var(--offset));
  width: calc(50vw + 100px - var(--offset));
  height: 450px;
  clip-path: polygon(0 0, 0 50px, 100% 450px, 100% 200px, 100px 0, 0 0);
}

div#extra-div-6 {
  top: 450px;
  left: 0px;
  width: calc(50vw + var(--offset));
  height: 800px;
  clip-path: polygon(0 300px, 0 800px, 100% 50px, 100% 0, calc(100% - 100px) 0);
}

html {
  overflow-x: hidden;
}

:root {
  --main: #B047A0;
  --sub: #FF7F00;
  --offset: 10vw;

  --header-logo: url(http://scpko.wdfiles.com/local--files/samcheonri-contest-chungnam/cs-logo.png) 0px 35px no-repeat;

  --color-bg: repeat-x center/100% url("http://scpko.wdfiles.com/local--files/samcheonri-contest-chungnam/cs.png");
}

#breadcrumbs {
  display: none;
}

 #search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
     background: #FFF;
}

 h2, h3 {
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
}

 #header h1 a::before {
     color: #FFF;
     text-shadow: 0 -3px var(--main), 0 3px var(--main), 3px 0 var(--main), -3px 0 var(--main), 2px 2px var(--main), 2px -2px var(--main), -2px 2px var(--main), -2px -2px var(--main);
}
.item {
  order: 1;
  border-radius: 5px;
  background: var(--sub);
  font-size: 110%;
  font-weight: 700;
  text-align: center;
  width: auto;
  margin: 5px;
  padding: 0 15px 0 15px;
  color: #FFF;
}

.item, .item a, .item a:visited,
.site, .site a, .site a:visited {
  color: #FFF;
}

.loc {
  position: absolute;
  transform: translate(-20px, -7px);
  background: #FFF;
  border: 2px solid var(--sub);
  padding: 4px;
  border-radius: 15px;
  color: var(--main);
  font-size: 80%;
  vertical-align: center;
  text-align: center;
}

span strong {
  color: var(--main);
}

ul ul, li ul {
  margin-left: -10px;
}

.footnotes-footer .title {
  display: none;
}

@media (min-width: 768px) {
  :root {
    --offset: 16em;
  }
}

@media (max-width: 676px) {
  :root {
    --color-bg: no-repeat center/auto 100% url("http://scpko.wdfiles.com/local--files/samcheonri-contest-chungnam/cs.png");
  }
}

