[[module CSS]]
:root {
  --main: #00A885;
  --sub: #FEB827;

  --header-logo: url(http://scpko.wdfiles.com/local--files/samcheonri-contest-gangwon/ga-logo.png) 0px 35px no-repeat;

  --color-bg: repeat-x center/100% url("http://scpko.wdfiles.com/local--files/samcheonri-contest-gangwon/ga.png");
}

html, body {
  max-width: 100%;
  overflow-x: hidden;
}

#breadcrumbs {
  display: none;
}

 #search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
     background: #FFF;
}

.rate-box-with-credit-button, .page-rate-widget-box {
  background: var(--main);
}

 h2, h3 {
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
}

 #header h1 a::before {
     color: #FFF;
     text-shadow: 0 -3px var(--main), 0 3px var(--main), 3px 0 var(--main), -3px 0 var(--main), 2px 2px var(--main), 2px -2px var(--main), -2px 2px var(--main), -2px -2px var(--main);
}
.item {
  order: 1;
  border-radius: 5px;
  background: var(--sub);
  font-size: 110%;
  font-weight: 700;
  text-align: center;
  width: auto;
  margin: 5px;
  padding: 0 15px 0 15px;
  color: var(--main);
}

.loc {
  position: absolute;
  transform: translate(-20px, -7px);
  background: #FFF;
  border: 2px solid var(--sub);
  padding: 4px;
  border-radius: 15px;
  color: var(--main);
  font-size: 80%;
  vertical-align: center;
  text-align: center;
}

ul ul, li ul {
  margin-left: -10px;
}

.footnotes-footer .title {
  display: none;
}

.nowrap { white-space: nowrap; word-break: keep-all; }

@media (max-width: 676px) {
  :root {
    --color-bg: no-repeat left center /auto 100% url("http://scpko.wdfiles.com/local--files/samcheonri-contest-gangwon/ga.png");
  }
}

@media (max-width: 480px) {
  .nowrap { white-space: wrap; word-break: break-all; }
}
[[/module]]

[[module CSS]]
@font-face {
    font-family: 'Tenada';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2210-2@1.0/Tenada.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
}

#content-wrap, #main-content { position: static; }

.first { animation: 20s linear 1s infinite chu-chu; }
.second { animation: 20s linear 11s infinite chu-chu; }

#page-content div.train {
  position: absolute; top: 380px; max-width: 1750px; width: 1750px;
  height: 200px; left: 200000px;
}

#page-content div .traincar {
  position: absolute; top: 0; max-width: 906px; width: 906px; height: 200px;
}

.traincar:nth-child(2) { left: 850px }
.traincar:nth-child(3) { left: 1700px; display: none; }
.traincar:nth-child(4) { left: 2550px; display: none; }
.traincar:nth-child(5) { left: 3400px; display: none; }
.traincar:nth-child(6) { left: 4250px; display: none; }

.traincar .smoke { position: absolute; top: -100px; left: 0; width: 100%; height: 150px; }

.traincar .smoke img { filter: opacity(0); }

#page-content div.wheels {
  position: absolute; top: 145px; width: 45px; height: 45px;
  animation: 1s linear 0s infinite wheel;
}

.wheels:nth-of-type(1) { left: 182px; }
.wheels:nth-of-type(2) { left: 247px; }
.wheels:nth-of-type(3) { left: 313px; }
.wheels:nth-of-type(4) { left: 378px; }

#page-content div.rod-wheel {
  position: absolute; top: 162px; left: 306px; width: 55px; height: 10px; border-radius: 5px;
  animation: 1s linear 0s infinite rod;
}

#page-content div.rod {
  position: absolute; top: 0; left: -60px; max-width: 135px; width: 135px; height: 10px; border-radius: 5px;
  background: #202020; box-shadow: 0 0 4px #EEE; animation: 1s linear 0s infinite reverse rod;
}

#page-content div.crank {
  position: absolute; top: -2.5px; left: 40px; max-width: 150px; width: 150px; max-height: 15px; height: 15px; border-radius: 7px; transform-origin: 5px 5px;
  background: #202020; animation: 1s linear 0s infinite crank;
}

#page-content div.deco {
  position: absolute; top: 5px; left: 5px; max-width: 140px; width: 140px; height: 5px; border-radius: 5px; background: #FED278;
}

#page-content div.piston {
  position: absolute; top: 162px; width: 50px; height: 10px;
  background: #202020; animation: 1s ease-in-out 0s infinite piston;
}

#page-content div.carriedtitle {
  position: absolute; top: 40px; left: 50%; transform: translateX(-50%); height: 100px; background-color: #202020; color: var(--sub); text-align: center; font-size: 128px; font-family: 'Tenada';
}

#page-content div.kor { width: 360px; }
#page-content div.eng { width: 625px; }


@media (max-width: 580px) {
  .first { animation: 9s linear 1s infinite ch; }
  .second { display: none; }
}

@media (max-width: 767px) and (min-width: 581px) {
  .train { width: 905px !important; }
  .first { animation: 9s linear 1s infinite chu; }
  .first .traincar:nth-child(2) { display: none; }
  .second { animation: 9s linear 5.4s infinite chu; }
  .second .traincar:first-child { display: none; }
  .second .traincar:nth-child(2) { display: none; }
  .second .traincar:nth-child(3) { display: block; left: 0; }
}

@media (max-width: 1649px) and (min-width: 768px) {
  .first { animation: 15s linear 1s infinite chchu; }
  .second { animation: 15s linear 6s infinite chchu; }
  .second .traincar:first-child { display: none; }
  .second .traincar:nth-child(2) { left: calc( 1020px - 22vw ); }

  #side-bar {
    top: 194px;
    left: 2em;
  }
}

@media (min-width: 1040px) {
  #side-bar {
    top: 194px;
    left: 50%;
    transform: translateX( -495px );
  }
}

@media (min-width: 768px) {
  #side-bar .side-block {
    background: none !important;
  }
  .side-block:nth-child(3) div:nth-child(1),
  .side-block:nth-child(3) div:nth-child(2),
  .side-block:nth-child(3) div:nth-child(3),
  .side-block:nth-child(3) div:nth-child(4),
  .side-block:nth-child(3) div:nth-child(5),
  .side-block:nth-child(3) div:nth-child(6),
  .side-block:nth-child(3) div:nth-child(7),
  .side-block:nth-child(3) div:nth-child(8),
  .side-block:nth-child(3) div:nth-child(9),
  .side-block:nth-child(3) div:nth-child(10) {
    opacity: 0;
    transition: all 1s;
  }
  #side-bar {
    z-index: 100;
    background: none;
    transition: all 1s;
  }
  #side-bar:hover {
    background: #FFF;
    box-shadow: 5px 0 10px #FFF, -5px 0 10px #FFF;
    transition: all 1s;
  }
  #side-bar:hover .side-block:nth-child(3) div {
    opacity: 1;
    transition: all 1s;
  }
}

@media (min-width: 2880px) {
  .traincar:nth-child(3) { display: block; }

  .first { animation: 24s linear 1s infinite chu-chu-chu; }
  .second { animation: 24s linear 13s infinite chu-chu-chu; }
}

@media (min-width: 3840px) {
  .traincar:nth-child(4) { display: block; }

  .first { animation: 28s linear 1s infinite chu-chu-chu-chu; }
  .second { animation: 28s linear 15s infinite chu-chu-chu-chu; }
}

@media (min-width: 4800px) {
  .traincar:nth-child(5) { display: block; }

  .first { animation: 32s linear 1s infinite chu-chu-chu-chu-chu; }
  .second { animation: 32s linear 17s infinite chu-chu-chu-chu-chu; }
}

@media (min-width: 5760px) {
  .traincar:nth-child(6) { display: block; }

  .first { animation: 36s linear 1s infinite chu-chu-chu-chu-chu-chu; }
  .second { animation: 36s linear 19s infinite chu-chu-chu-chu-chu-chu; }
}

@keyframes ch {
  0% { left: 100%; }
  100% { left: -1750px; }
}

@keyframes chu {
  0% { left: 100%; }
  90% { left: -905px; }
  100% { left: -905px; }
}

@keyframes chchu {
  0% { left: 100%; }
