}

@keyframes pist {
  0% { left: min(6.77vw, 130px); }
  50% { left: min(8.85vw, 170px); }
  100% { left: min(6.77vw, 130px); }
}

@keyframes piston {
  0% { left: 130px; }
  50% { left: 170px; }
  100% { left: 130px; }
}

@keyframes crank {
  0% { transform: rotate(0deg); }
  25% { transform: rotate(10deg); }
  50% { transform: rotate(0deg); }
  75% { transform: rotate(-10deg); }
}

@keyframes rod {
  0% { transform: rotate(360deg); }
  100% { transform: rotate(0deg); }
}

@keyframes wheel {
  0% { transform: rotate(270deg); }
  100% { transform: rotate(-90deg); }
}

@keyframes smoke1 {
  0% {
    width: 0px; height: 0px;
    top: 105px; left: 140px;
    filter: blur(5px) opacity(0) grayscale(1);
  }
  10% {
    width: 50px; height: 80px;
    top: 35px; left: 130px;
    filter: blur(2px) opacity(0.4) grayscale(1);
  }
  25% {
    width: 120px; height: 100px;
    top: 5px; left: 140px;
    filter: blur(2px) opacity(0.3) grayscale(1);
  }
  70% {
    width: 220px; height: 90px;
    top: 20px; left: 180px;
    filter: blur(5px) opacity(0.1) grayscale(1);
  }
  100% {
    width: 0px; height: 100px;
    top: 50px; left: 250px;
    filter: blur(8px) opacity(0) grayscale(1);
  }
}

@keyframes smoke2 {
  0% {
    width:120px; height: 70px;
    top: 0px; left: 160px;
    filter: blur(6px) opacity(0) grayscale(1);
  }
  50% {
    width:300px; height: 100px;
    top: 20px; left: 210px;
    filter: blur(5px) opacity(0.43) grayscale(1);
  }
  100% {
    width:400px; height: 110px;
    top: 35px; left: 320px;
    filter: blur(10px) opacity(0) grayscale(1);
  }
}

@keyframes smoke3 {
  0% {
    width:200px; height: 120px;
    top: 30px; left: 300px;
    filter: blur(8px) opacity(0) grayscale(1);
  }
  20% {
    width:250px; height: 130px;
    top: 45px; left: 400px;
    filter: blur(10px) opacity(0.15) grayscale(1);
  }
  100% {
    width:300px; height: 100px;
    top: 50px; left: 550px;
    filter: blur(18px) opacity(0) grayscale(1);
  }
}

@keyframes smoke4 {
  0% {
    width:300px; height: 150px;
    top: 60px; left: 550px;
    filter: blur(12px) opacity(0) grayscale(1);
  }
  30% {
    width:550px; height: 160px;
    top: 65px; left: 600px;
    filter: blur(9px) opacity(0.1) grayscale(1);
  }
  60% {
    width:750px; height: 140px;
