@import url(https://fonts.googleapis.com/css2?family=Playfair+Display+SC:wght@400;700;900&display=swap);
@import url(https://fonts.googleapis.com/css2?family=Texturina&display=swap);
@import url(https://fonts.googleapis.com/css2?family=Recursive&display=swap);

/* == LICENSING ==
 * Background Images, Colour Variables, and Fonts Are All From:
 *	The Dreamers' Library Theme by LAN 2D
 * Source Link:
 *	https://wanderers-library.wikidot.com/component:dreamers-theme
**/

/* ==== HEADER AREA ==== */
/* == COMMON == */
 :root { /* == FLOPSTYLE: LITE + CENTERED HEADER == */
	--header-title: "The Wanderers’ Library";
	--header-subtitle: none;

	--lgurl: url(https://wanderers-library.wikidot.com/local--files/component:dreamers-theme/Oma-logo-22042013_300pp.png);

	--onyx: 20, 20, 20;
}

 :root { /* == DUSTJACKET == */
	--body-font: "Texturina", serif;
	--header-font: "Playfair Display SC", serif;
	--title-font: var(--header-font);
	--mono-font: "Recursive", sans-serif;

	--white-monochrome: 252, 252, 252;
	--pale-gray-monochrome: 161, 161, 161;
	--gray-monochrome: 79, 79, 79;
	--dark-gray-monochrome: 82, 82, 82;
	--bright-accent: 235, 235, 235;
	--medium-accent: 163, 162, 160;
	--dark-accent: var(--dark-gray-monochrome);
	--alt-accent: 253, 206, 175;
}

 body {
	color: rgb(var(--bright-accent));
	background: rgb(var(--dark-gray-monochrome));
}

 #content-wrap::before {
	content: "";
	position: absolute;
  	height: 100%;
	width: 100%;

	background-repeat: repeat-x;
	background-image: url(https://wanderers-library.wikidot.com/local--files/component:dreamers-theme/greyglow3.png);
	opacity: .7;
}

 #content-wrap {
	max-width: 100%;
	margin-top: 0;
	padding-bottom: 1rem;

	background-image: url(https://wanderers-library.wikidot.com/local--files/component:dreamers-theme/backgroundfinal4.png);
}

 #page-content {
	padding-top: 1.5rem;
}

 @media (min-width: 768px) {
 	 #page-content {
		font-size: initial;
	}
}

/* == SITE BANNER == */
 div#extra-div-1 {
	background-image: url(https://wanderers-library.wikidot.com/local--files/component:dreamers-theme/baloon-bg.png);
	background-size: auto 140px;
	mix-blend-mode: normal;

	z-index: 1;
}

 #header::before {
	opacity: .9;
}

 #header h1 a::before {
	font-family: unset;
	-webkit-text-fill-color: rgb(var(--bright-accent));
	text-shadow:
		0 0 10px black,
		0 0 20px white;
}

/* == SIDE-BAR == */
 #top-bar .open-menu a {
	color: rgb(var(--bright-accent));
	background: rgb(var(--dark-gray-monochrome));
	border-color: rgb(var(--bright-accent));
}

/* ==== CONTENT AREA ==== */
/* == INFO BAR == */
 .info-container {
	--barColour: rgb(var(--onyx));
	--linkColour: rgb(var(--pale-gray-monochrome));
}

/* == PAGE RATING == */
 div.page-rate-widget-box .rate-points {
	border-color: inherit;
}

/* == FORMATTING | [GENERAL] == */
 #page-content h1,
 #page-content h2 {
	justify-content: start;
	margin: 0;
	padding: 0;

	text-align: left;
}

 h1 {
	font-weight: 900;
	letter-spacing: 1px;
}

 h2 {
	font-weight: 400;
}

 #page-title {
	display: none;
}

 #main-content hr {
	height: .125rem;

	background: rgb(var(--dark-gray-monochrome));
}

 #main-content .entry hr {
	height: .0625rem;

	background: rgba(var(--white-monochrome), .75);
}

 a,
 a.newpage,
 a:visited {
	color: rgb(var(--alt-accent));
}

/* == CUSTOM DIV BLOCKS == */
 .entry {
	margin-bottom: calc(5rem / 3);
	padding: calc(5rem / 3);

	background: rgba(var(--pale-gray-monochrome), .25);
	--pale-gray-monochrome: 149, 178, 149;
	border: 2px solid rgba(var(--white-monochrome), .75);
}

 .wanderers-memo,
 .section,
 .footnotes-footer {
	padding: calc(4rem / 3);

	background: rgba(var(--pale-gray-monochrome), .25);
	border-color: rgba(var(--white-monochrome), .75);
	border-style: solid;

	overflow: auto;
}

 .wanderers-memo {
	--pale-gray-monochrome: 178, 176, 149;
	border-width: 2px;
}

 .section,
 .footnotes-footer {
	margin-bottom: .2rem;

	--pale-gray-monochrome: 141, 165, 141;
	border-width: 1px;
}

 .lightweight {
	background: rgba(var(--dark-gray-monochrome), .25);
	border: 2px solid rgba(var(--white-monochrome), .25);
}

/* == FORMATTING | [SPECIAL] == */
 .fancyborder {
	border-image-source: url(https://wanderers-library.wikidot.com/local--files/component:dreamers-theme/greyborder.png);
}

/* == SITE FOOTER == */
 #footer,
 #footer a,
 #license-area,
 #license-area a {
	color: rgb(var(--bright-accent));
}

 #footer {
	margin-top: 0;
}
[[/module]]
