[[module CSS]]

div#container-wrap {
    background-image: url('https://scp-wiki.wdfiles.com/local--files/theme:skiesmadestrange/body_bg.png');
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'Sacrifice Confers Power';
    color: #efefef;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

div.page-rate-widget-box .rate-points {
    background-color: #132639;
    border: solid 1px #0b65c0;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: 132639;
    border-top: solid 1px #0b65c0;
    border-bottom: solid 1px #0b65c0;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: 132639;
    color: #0b65c0;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #132639;
    color: #0b65c0;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #132639;
    border: solid 1px #0b65c0;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #0b65c0;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #132639;
    color: #fffff0;
    text-decoration: none;
}


h1, #page-title {
    color: #0c499c;
}


a {
    color: #0c499c;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #0c499c;
    text-decoration: none;
    background: transparent;
}

#side-bar .side-block {
    border-color: #0e7dcd;
    box-shadow: 0 2px 6px rgba(0,0,51,.5);
}

#side-bar .heading {
    color: #0e7dcd;
    border-bottom: solid 1px #0e7dcd;
}

#side-bar div.menu-item.inactive a {
    color: #0b65c0;
}

#side-bar a {
    color: #0c499c;
}

#side-bar div.menu-item .sub-text {
