[[module CSS]]
.page-rate-widget-box{

}

.page-rate-widget-box .rate-points{
    background-color: #666;
    color: #FFF;
    font-weight: bold;
    padding: 0 10px;
    -moz-border-radius: 4px 0 0 4px;
}

.page-rate-widget-box a{
    text-decoration: none;
    color: #000;
    background-color: #DDD;
    padding: 0 5px;
}
.page-rate-widget-box a:hover{
    background-color: #EEE;
}

.page-rate-widget-box .cancel, .page-rate-widget-box .cancel a{
    -moz-border-radius:  0 4px 4px  0;
}
.inset {
    float: right;
    width: 25%;
    margin: 0px;
    border-style: solid;
    border-radius: 5px;
    border-width: 2px;
    border-color: #ee9999;
    padding: 10px;
    margin-left: 10px;
    margin-top: 5px;
    margin-bottom: 5px;
    background: #FFEEEE;
    font-size: 90%;
}
.inset > p {
    margin: 0;
}
.space {
float:center; width:100%; border:solid 3px #ededfd; padding:10px; margin-bottom: 10px;margin:0 auto;
