[[module CSS]]

#header, #side-bar, #footer, #license-area,
#action-area-top, #page-title, #breadcrumbs,
.ko-footer, .page-tags, #page-info-break,
#page-options-container, #action-area {
  display: none !important;
}

#container-wrap-wrap, div#container-wrap, #container {
  background: none;
  z-index: 100;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
}

#main-content {
  z-index: 200;
  position: fixed;
  top: 0;
  left: 0;
  margin: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  box-sizing: border-box;
}

#container {
  background-image: url('http://scp-jp-storage.wikidot.com/local--files/file:3438721-259-iotk/banner.jpg');
  background-size: cover;
  background-position: bottom;
  background-color: #0E183C;
}

.page-rate-widget-box {
  background-color: #EEEFFF;
  border: solid 1px #EEEFFF;
}

.page-rate-widget-box .rate-points {
  background-color: #EEEFFF;
  border: solid 1px #EEEFFF;
  color: #049;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
  background-color: #EEEFFF;
  border-top: solid 1px #EEEFFF;
  border-bottom: solid 1px #EEEFFF;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
  color: #049;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
  background: #0060DF;
  color: #FFF;
}
.page-rate-widget-box .cancel {
  background-color: #EEEFFF;
  border: solid 1px #EEEFFF;
}
.page-rate-widget-box .cancel a {
  color: #049;
}
.page-rate-widget-box .cancel a:hover {
  background: #0060DF;
  color: #FFF;
}

.rating-floater {
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  height: 100px;
  box-sizing: border-box;
  padding: 30px 30px 0 0;
  text-align: right;
}

.atag, .atag:hover {
  position: fixed;
  top: 100px;
  left: 0;
  width: 100%;
  height: calc( 100% - 150px );
  display: flex;
  text-align: center;
  box-sizing: border-box;
  text-decoration: none;
  align-items: flex-end;
  align-content: flex-end;
  flex-wrap: wrap;
  justify-content: center;
  text-shadow: 0 -3px 1.5px #049, 0 3px 1.5px #049, 3px 0 1.5px #049, -3px 0 1.5px #049, 2px 2px 1.5px #049, 2px -2px 1.5px #049, -2px 2px 1.5px #049, -2px -2px 1.5px #049;
}

.atag, .atag span {
  font-size: 110%;
}

@media (max-width: 1024px) and (max-width: 100vh) {
  #container {
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
  }
  .atag, .atag span {
    font-size: unset;
  }
}

@media (max-width: 767px) {
  .rating-floater {
    float: right;
    padding: 30px 15px 0 0;
  }
}
/*
@media (min-width: 1921px) {
  #container {
    background-size: contain;
