  background-image: url(http://scpkrsandbox.wdfiles.com/local--files/1000kocontest-thd-glasses/corner0.png), url(http://scpkrsandbox.wdfiles.com/local--files/1000kocontest-thd-glasses/corner2.png), url(http://scpkrsandbox.wdfiles.com/local--files/1000kocontest-thd-glasses/corner3.png), url(http://scpkrsandbox.wdfiles.com/local--files/1000kocontest-thd-glasses/corner4.png);
