[[include :scp-wiki:component:toggle-sidebar]]

[[include :scpko:theme:anniversary
|custom=1|bg=#FFF|ip=#000|font=#000
|subtitle=Veterans Affairs Department
]]

[[module CSS]]

:root {
  --header-logo: url(http://scpko.wdfiles.com/local--files/theme%3Aanniversary/anniversary_h.png) 0px 35px no-repeat;

  --color-bg: #FFF;
  --color-ip: #000;
  --font: #000;
  --shadow: transparent;
  --header-sub: "Veterans Affairs Department";
}

@font-face {
    font-family: 'GowunBatang-Bold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2108@1.1/GowunBatang-Bold.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

/*
sigma-9 기반 n주년 연말경연 테마
*/

/* COMMON */
 #content-wrap {
     min-height:1700px;
     height: auto;
}

 h1, #page-title {
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
     color: var(--color-ip);
}

 #page-title {
     border-color: var(--color-ip);
}

/* links */

 a {
     color: var(--color-ip);
}

 a:visited, #side-bar a:visited {
     color: var(--color-ip);
}

 #page-content a.newpage, #side-bar a.newpage {
     color: var(--color-ip);
     filter: opacity(60%);
}

 body {
     background-color: #FFF;
     color: #000;
}

/* HEADER */
div#container-wrap {
    background: none;
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: var(--color-bg);
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: var(--color-ip);
}

 #search-top-box-input {
     border: transparent;
     color: var(--font);
     background-color: var(--color-bg);
}

 #search-top-box-input:hover, #search-top-box-input:focus {
     border: solid 0.5px #888;
     color: var(--color-ip);
     background-color: var(--color-bg);
}

 #search-top-box-form input[type=submit] {
     border: transparent;
     color: var(--font);
     text-shadow: 0 0 3px var(--shadow);
     background: none !important;
     background-color:  var(--color-bg);
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
     border: solid 0.5px #FFF;
     color: var(--color-ip);
     background: none !important;
     background-color: var(--color-bg);
}

 #login-status {
     color: var(--font);
     text-shadow: 1px 0 var(--shadow), -1px 0 var(--shadow), 0 1px var(--shadow), 0 -1px var(--shadow);
}

 #login-status a {
     color: var(--font);
     text-shadow: 1px 0 var(--shadow), -1px 0 var(--shadow), 0 1px var(--shadow), 0 -1px var(--shadow);
}

 #login-status ul a {
     color: #444;
     text-shadow: none;
}

 #header  {
     background: none;
}

 div#header-extra-div-1 {
     background: var(--header-logo);
     background-size: 100px;

     position: absolute;
     top: 0px; bottom: 0px; left: 0px;
     width: 100px;
}

 #header h1 a {
     color: transparent;
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
     font-size: 150%;
     text-shadow: none;
     letter-spacing: 0.5px;
}

 #header h1 a::before {
     content: "SCP 재단";
     color: var(--color-ip);
     text-shadow: none;
}

 #header h2 span {
     color: transparent;
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
     font-size: 120%;
     text-shadow: none;
     margin-left: 5px;
}

 #header h2 span::before {
     content: var(--header-sub);
     color: var(--font);
     text-shadow: 1.5px 0 var(--shadow), -1.5px 0 var(--shadow), 0 1.5px var(--shadow), 0 -1.5px var(--shadow), 1px 1px var(--shadow), -1px 1px var(--shadow), 1px -1px var(--shadow), -1px -1px var(--shadow);
}

 #top-bar a {
     color: #FFF;
}

 #top-bar ul li ul {
     border: solid 1px #EEE;
     box-shadow: none;
}

 #top-bar ul li.sfhover a, #top-bar ul li:hover a {
     background: #EEE;
     color: #444;
}

 #top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
     border-top: 1px solid #EEE;
}

 #top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
     background: #DDD;
}

 #top-bar ul li ul a, #top-bar a:hover, .c_topbar_arrow {
     color: #444;
}

 #top-bar ul li ul li ul {
     border: none;
}

/* SIDE MENU */
 #side-bar {
     background: #FFF;
}

 #side-bar .side-block .menu-item img {
  display: none;
}

 #side-bar div.side-block:nth-child(4) > div:nth-child(1) > div:nth-child(1) img {
     filter: none;
     -webkit-filter: none;
}

#side-bar .side-block {
  border: none;
  background: #FFF !important;
  box-shadow: none !important;
}

 #side-bar .heading {
     color: var(--color-ip);
     border-bottom: solid 1px var(--color-ip);
}

 #side-bar div.menu-item.inactive a {
     color: var(--color-ip);
}

 #side-bar div.menu-item .sub-text {
     color: var(--color-ip);
}

 #side-bar .collapsible-block-folded {
     background: #FFF;
}

 #side-bar .collapsible-block-unfolded-link {
     border-bottom: solid 1px var(--color-ip);
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     color: var(--color-ip);
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: var(--color-ip);
}

/* YUI-TABS */
 .yui-navset .yui-content{
     background-color: #FFF;
}

 .yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background: #EEE url(none) repeat-x;
    border-top: solid #333 1px;
    border-left: solid #444 1px;
    border-right: solid #444 1px;
    color:#000;
}

 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
 .yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
     background: #444; /* selected tab background */
     color: #EEE;
}

 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus {
     background: #DDD;
     text-decoration: none;
}
 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav {
     border-color: #444;
}

/* Rating Module */
 .page-rate-widget-box{
     box-shadow: none;
}

 .page-rate-widget-box .rate-points {
     background-color: var(--color-bg) !important;
     border: solid 1px var(--color-bg);
     color: var(--font);
}

 .page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
     background-color: var(--color-bg);
     border-top: solid 1px var(--color-bg);
     border-bottom: solid 1px var(--color-bg);
}

 .page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
     color: var(--font);
}

 .page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
     background: var(--color-bg);
     color: var(--color-ip);
}

 .page-rate-widget-box .cancel {
     background-color: var(--color-bg);
     border: solid 1px var(--color-bg);
}

 .page-rate-widget-box .cancel a {
     color: var(--font);
}

 .page-rate-widget-box .cancel a:hover {
     background-color: var(--color-bg);
     color: var(--color-ip);
}

/* Info Rating Module */

 .rate-box-with-credit-button {
     background-color: var(--color-bg) !important;
     border: 1px solid var(--color-bg) !important;
     border-radius: 5px !important;
     box-shadow: none !important;
}

 .rate-box-with-credit-button .creditButton p a {
     border-left-color: var(--color-bg) !important;
     color: var(--font);
}

 .rate-box-with-credit-button .creditButton p a:hover {
     color: var(--color-ip);
}

 .rate-box-with-credit-button .page-rate-widget-box .cancel {
     border-radius: 0;
}

 .rate-box-with-credit-button .page-rate-widget-box .rate-points {
     border-left: 0;
}

/* Heritage Collection Rating Module */

 .heritage-rating-module {
     background-color: var(--color-bg);
     box-shadow: none;
}

/* Mobile Media Query */
 @media (max-width: 479px) {
    :root {
         --header-sub: "VA Department";
    }

     div#header-extra-div-1 {
         background-position: 0 4.5em;
         background-size: 60px 60px;
         width: 60px;
    }
     #header h1 a {
         font-size: 100%;
    }
     #header h1, #header h2 {
         margin-left: 66px;
    }
}
/* Small Mobile Media Query */
 @media (max-width: 385px) {
     div#header-extra-div-1 {
         background-position: 5% 5.5em;
    }
     #header h1, #header h2 {
         margin-left: -webkit-calc(66px + 5%);
         margin-left: -moz-calc(66px + 5%);
         margin-left: calc(66px + 5%);
    }
}
/* Note Media Query */
 @media (min-width: 480px) and (max-width: 580px) {
     div#header-extra-div-1 {
         background-position: 0.5em 4.5em;
         background-size: 66px 66px;
         width: 72px;
    }
     #header h1 a {
         font-size: 110%;
    }
     #header h1, #header h2 {
         margin-left: 80px;
    }
}
/* Mini Tablet Media Query */
 @media (min-width: 581px) and (max-width: 767px) {
     div#header-extra-div-1 {
         background-position: 0.5em 3.5em;
         background-size: 77px 77px;
         width: 85px;
    }
     #header h1 a {
         font-size: 120%;
    }
     #header h1, #header h2 {
         margin-left: 93px;
    }
}
/* Tablet Media Query */
 @media (min-width: 768px) and (max-width: 979px) {
     div#header-extra-div-1 {
         background-size: 88px 88px;
         width: 88px;
    }
     #header h1, #header h2 {
         margin-left: 106px;
    }
}



