[[module CSS]]
@import url(//fonts.googleapis.com/earlyaccess/jejumyeongjo.css);

@font-face {
    font-family: 'Daehan-Bold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_twelve@1.1/Daehan-Bold.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

:root {
  --header-logo: url(http://scpko.wdfiles.com/local--files/main/logo_white.png) 0px 35px no-repeat;
  --barColour: #000;
  --gap: 6px;
  --width: 14px;
}

#header h1 a::before {
   content: "SCP Foundation";
}

#header h2 span {
  margin-left: 20px;
}

#page-title, #breadcrumbs {
  display: none;
}

div#extra-div-2 {
  height: 34px;
  z-index: -1;
}

#main-content {
  margin-top: -20px;
}

.info-container .collapsible-block-unfolded {
  border-color: rgba(0, 0, 0, 0.8);
}

.info-container .collapsible-block-unfolded {
  height: 430px;
}

.info-container .collapsible-block-unfolded-link {
  transform: translateY(calc(430px - 1.5em)) rotate(180deg);
  mask-size: calc(100% + 10px);
}

.info-container .collapsible-block-unfolded-link .collapsible-block-link {
  transform: rotate(180deg);
}

.award {
  z-index: 1;
  position: relative;
  width: 100%;
  text-align: center;
  box-sizing: border-box;
  padding: 70px 11% 70px 11%;
  border-left: var(--width) solid #000;
  border-right: var(--width) solid #000;
}

.award::before {
  content: "";
  position: absolute;
  top: 0;
  left: var(--gap);
  width: calc( 100% - 2*var(--gap) );
  height: 100%;
  border-left: var(--width) solid #000;
  border-right: var(--width) solid #000;
  box-sizing: border-box;
}

.award::after {
  content: "";
  position: absolute;
  top: -2px;
  left: calc( var(--width) * (-1) );
  width: calc( 100% + 2*var(--width) + 1px );
  height: calc( 100% + 4px );

  background-repeat: no-repeat;
  background-size: 50.1%;
  background-image: url(http://scpko.wdfiles.com/local--files/scp-1000-ko/corner0.png), url(http://scpko.wdfiles.com/local--files/scp-1000-ko/corner2.png), url(http://scpko.wdfiles.com/local--files/scp-1000-ko/corner3.png), url(http://scpko.wdfiles.com/local--files/scp-1000-ko/corner4.png);
  background-position: top left, top right, bottom left, bottom right;
}

.award .star {
  z-index: 10;
  position: relative;
  width: 50%;
  height: 0;
  padding: 25% 0;
  margin: 0 auto;
  margin-bottom: 1em;

  background-repeat: no-repeat;
  background-size: 100%;
  background-position: center;
  background-image: url(http://scpko.wdfiles.com/local--files/component:thd-achievement-base/100.png);
}

.award h1 span, .award h2 span, .award h3 span,
.award h1 span span, .award h2 span span, .award h3 span span {
  z-index: 20;
  position: relative;
  line-height: 2;
  word-break: keep-all;
  font-weight: normal;
}

.award h1 span {
  color: #000;
  text-shadow: 0.3px 0.3px 0.1px #000, 0.3px -0.3px 0.1px #000, -0.3px 0.3px 0.1px #000, -0.3px -0.3px 0.1px #000;
}

.award h2 span {
  font-family: 'Jeju Myeongjo', 'Nanum Gothic', serif;
}

.award h3 span {
  font-family: 'Daehan-Bold';
}

.award h1, .award h2, .award h3 {
  margin-top: 0;
  margin-bottom: 0;
}

.award h1 span span, .award h2 span span, .award h3 span span, .message strong span {
  white-space: nowrap;
  word-break: keep-all;
}

.page-rate-widget-box {
  z-index: 15;
  position: absolute;
  top: 47px;
  right: 45px;
}

div.page-rate-widget-box,
div.page-rate-widget-box .rate-points,
div.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown,
div.page-rate-widget-box .cancel {
  background-color: transparent !important;
  border: none;
  box-shadow: none;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  color: #FFF;
  text-shadow: 0 0 2px black;
  border-radius: 0;
}

.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover {
  background: #FFF;
  color: #000;
  text-shadow: none;
  border-radius: 0;
}

#action-area .page-rate-widget-box {
  position: static;
  top: unset;
  right: unset;
}

.message p {
  font-family: 'Jeju Myeongjo', 'Nanum Gothic', serif;
  font-size: 1.3em;
  line-height: 1.8;
  word-break: keep-all;
  text-align: justify;
}

.message strong {
  display: inline-block;
  margin-top: -1.65em;
  background: #FFF;
  padding: 0 15px;
  line-height: 1;
}

.message .smalltitle strong {
  font-size: 120%;
  line-height: 1.5;
}

#top-bar .open-menu a {
  border: 0.2em solid #000;
  border-radius: 0;
  color: #000;
}

.info-container .collapsible-block-content {
  text-align: center;
}

.translation_block {
  display: none;
}


@media (max-width: 767px) {
  :root {
    --gap: 1.1vw;
    --width: 2.2vw;
  }
  #header h2 span {
    margin-left: 10px;
  }
  .award::after {
    background-size: 65%;
  }
  .award {
    padding: 15vw 12% 70px 12%;
  }
  .page-rate-widget-box {
    top: calc( 3.2*var(--width) );
    right: calc( 2.5*var(--width) );
  }
  .message {
    text-align: left;
  }
}

@media (max-width: 580px) {
  #header h2 span {
    margin-left: 0;
  }
  #search-top-box-input {
    display: none;
  }
  .award {
    padding: 15vw 10% 70px 10%;
  }
  .award h3 span span.divide,
  .message strong span.divide {
    white-space: normal;
  }
  .award h3 span span.divide span,
  .message strong span.divide span {
    white-space: nowrap;
  }
  .message p {
    font-size: 1.15em;
    line-height: 1.7;
    word-break: normal;
  }
}
@media (max-width: 385px) {
  .award h3 span,
  .award h3 span span,
  .award h3 span span.divide span, {
    word-break: normal;
    white-space: normal;
  }
}
[[/module]]

[[div style="display: none;"]]
[[code type="css"]]
#interwiki a,
#side-bar a:visited, #interwiki a:visited,
#interwiki a.newpage {
  color: #000;
}
#interwiki .menu-item img {
  filter: brightness(0);
}

#interwiki .side-block {
  border: none;
  box-shadow: none;
}
