[[module CSS]]
:root:lang(ko) {
    --header-title: "SCP 재단 대한민국 지역사령부";
    --header-subtitle: "제01K기지 내부 데이터베이스";
    --logo-image: url("http://scpkosb.wikidot.com/local--files/draft:agent-s-k-23-9cea/SCPKO1.png");
    --barColour: #2C5468;
    --timeScale: 2;
    --timeDelay: 0.5s;
    --fnColor: #3C6478;
}
@media only screen and (max-width: 425px) {
:root:lang(ko) {
  --header-title: "SCP 재단 한국사령부";
}
}
.translation_block{ display: none; }
.u-faq{ display: none; }
::-webkit-scrollbar {
  width: 18px;
  background: #transparent;
}

body::-webkit-scrollbar-track, html::-webkit-scrollbar-track {
  background: #222222 repeating-linear-gradient(25deg,
     hsla(0, 0%, 100%, 0),
     hsla(0, 0%, 100%, 0) 0.25vh,
     rgba(240, 240, 240, 0.1) 0.35vh,
     rgba(240, 240, 240, 0.2) 0.5vh);}

body::-webkit-scrollbar-thumb, html::-webkit-scrollbar-thumb {
  background: #cccccc;
  border: #000000 1px solid;
}

body::-webkit-scrollbar-thumb:hover, html::-webkit-scrollbar-thumb:hover {
  background: #ffffff;
}
@media (max-width: 767px) {
::-webkit-scrollbar {
  width: 9px;
  height: 0px;
}
}

@keyframes fadeIn {
    from {
     opacity: 0;
     transform: translate(0,30px);
    }
    to {
     opacity: 1;
     transform: translate(0,0);
    }
}
#page-content blockquote {
  border: 2px double #999;
  background-color: #f4f4f4;
}

.blinkbar {animation: blink 1.5s infinite; letter-spacing: -0.13em;}@keyframes blink{to{opacity: .0;}}
[[/module]]

