[[module CSS]]
#page-title,
#header,
#side-bar {
    display: none;
}
#main-content {
    margin: 0 4em 0 12em;
}
div#container-wrap {
    background: none;
}
.all-wrap {
    font-family: Verdana, sans-serif;
    font-size: 13px;
}

.all-wrap h1 {
    color: #000;
    font-family: Verdana, sans-serif;
    font-size: 24px;
    margin: 0 0 15px;
    padding: 0;
}

.all-wrap a {
    color: #00f;
}

.all-wrap .content {
    max-width: 400px!important;
    padding-right: 20px;
    float: left;
    overflow: hidden;
}

.all-wrap tt {
    color: #666;
    font-family: monospace;
    font-size: smaller;
}

/* Flickr badge CSS */
.flickr_badge_uber_wrapper {text-align:center; width:260px; float: right;}
.flickr_badge_wrapper {padding:10px 0 10px 0;}
.flickr_badge_image {margin:0 10px 10px 10px;}
.flickr_badge_image img {border: 1px solid black !important;}
.flickr_badge_source {text-align:center; margin:0 10px 0 10px; font-size: 9px; color: #222}
.flickr_www {display:none;}
/* End of Flickr badge CSS */

@media (max-width: 979px) {
#page-content div, #page-content div table {
    float: none;
}
}

@media (max-width: 767px) {
#main-content {
    margin: 0 5%!important;
}
}
[[/module]]

