[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400..900&display=swap');

body {
    background: #EDEDED;
}

:root{
     --header-title: "FOUNDATION";
     --header-subtitle: " ";
     --titleColor: #FFFFFF;
     --subtitleColor: #FFFFFF;
}

a, .side-block, #action-area, #page-title, #search-top-box, #top-bar ul li {
    filter: grayscale(100%);
}

h1, h2, h3, h4, h5, h6 {
    font-family: Verdana;
}

.creditRate.creditModule {
    justify-content: center;
    margin: 0 auto .8em;
    width: 100%;
}

#page-content .rate-box-with-credit-button .creditButton {
    background-color: #000 !important;
    border: solid 1px #000 !important;
    padding: 1px 0;
}
#page-content .rate-box-with-credit-button .creditButton a:hover {
    color: #A5A5A5;
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #000 !important;
    border: solid 1px #000 !important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #000 !important;
    border-width: 1px 0 !important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #fff;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #fff !important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000 !important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent !important;
    color: #A5A5A5 !important;
}

div#container-wrap {
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A6903570-49-u3t0/SB-V-header.png) top left repeat-x;
}

#side-bar {
    background-color: rgb(134, 134, 134);
}

.scp-image-block img {
    width: 100%;
}
.scp-image-block .scp-image-caption {
    width: 100%;
}

div.obj {
    --swatch-text-light: 237, 237, 237;
}

.earthworm {
    width: calc(100% - 34px);
    margin-inline: auto !important;
}
.earthworm div:nth-child(1) {
    background: linear-gradient(to left, #eee, #fff);
}
.earthworm div:nth-child(3) {
    background: linear-gradient(to left, #fff, #eee);
}

.classified-bar-wrapper {
    position: relative;
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: repeat(2, 1fr);
    margin-bottom: 1em;
}
.classified-bar-text {
    position: absolute;
    font-family: "Orbitron", serif;
    font-weight: bold;
    font-size: 16px;
    white-space: nowrap;
    color: #FFFFFF;
    mix-blend-mode: difference;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
.level {
    position: relative;
    grid-area: 1 / 1 / 2 / 2;
    height: 60px;
    margin: 1px;
    background: linear-gradient(135deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-right: solid 3px #000000;
    clip-path: polygon(60px 0, 100% 0, 100% 100%, 0 100%);
}
.classified {
    position: relative;
    grid-area: 2 / 1 / 3 / 2;
    height: 60px;
    margin: 1px;
    background: linear-gradient(45deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-right: solid 3px #000000;
    clip-path: polygon(0 0, 100% 0, 100% 100%, 60px 100%);
}
.item-number {
    position: relative;
    grid-area: 1 / 2 / 2 / 3;
    height: 60px;
    margin: 1px;
    background: linear-gradient(-135deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-left: solid 3px #000000;
    clip-path: polygon(0 0, calc(100% - 60px) 0, 100% 100%, 0 100%);
}
.class {
    position: relative;
    grid-area: 2 / 2 / 3 / 3;
    height: 60px;
    margin: 1px;
    background: linear-gradient(-45deg, #FFFFFF calc(100% - 42.5px), #000000 calc(100% - 42.5px));
    border-top: solid 3px #000000;
    border-bottom: solid 3px #000000;
    border-left: solid 3px #000000;
    clip-path: polygon(0 0, 100% 0, calc(100% - 60px) 100%, 0 100%);
}
.icon {
    position: absolute;
    width: 60px;
    height: 60px;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: #EDEDED url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3Csvg version='1.2' baseProfile='tiny' id='Euclid' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 144 144' overflow='visible' xml:space='preserve'%3E%3Cpath d='M72,0C32.24,0,0,32.24,0,72c0,39.76,32.24,72,72,72c39.76,0,72-32.24,72-72C144,32.24,111.76,0,72,0z M97.8,36.17 l-13.7,13.7v-9.5c0,0,0-0.1,0-0.2l11.7-11.7C97.21,30.72,97.89,33.4,97.8,36.17z M84.2,21.87c2.5,0,4.8,0.6,6.8,1.8l-11.9,11.9h-9.7 l13.8-13.7H84.2z M46,39.67l17.8-17.8h9.7L46,49.37V39.67z M33.7,71.37c0.64-0.26,11.37-12.33,12.3-11.7c0-0.13,0-0.56,0-0.7 l13.7-13.7v9.7l-26.4,26.4C33.4,78.99,33,73.64,33.7,71.37z M33.3,91.07l31.6-31.6h9.7l-41.3,41.3C33.3,100.5,33.3,91.35,33.3,91.07 z M35.4,108.27l62.4-62.4v9.7l-57.7,57.7C38.2,111.97,36.6,110.27,35.4,108.27z M47.2,115.77l54.8-54.8c2.1,1,3.8,2.4,5.3,4.1 l-50.8,50.8C55.51,115.87,48,115.92,47.2,115.77z M110.7,100.27l-15.5,15.6h-9.7l25.2-25.3V100.27z M110.7,80.97l-34.8,34.9h-9.7 l44.2-44.2C110.9,73.7,110.64,78.81,110.7,80.97z'/%3E%3C/svg%3E%0A") center no-repeat;
    background-size: cover;
    border: solid 3px transparent;
    border-radius: 50%;
}

@media (max-width: 767px) {
  .classified-bar-text {
    font-size: 12px;
  }
}
