[[module CSS]]
:root {
  --main: #00A885;
  --sub: #FEB827;

  --header-logo: url(http://scpko.wdfiles.com/local--files/samcheonri-contest-gangwon/ga-logo.png) 0px 35px no-repeat;

  --color-bg: repeat-x center/100% url("http://scpko.wdfiles.com/local--files/samcheonri-contest-gangwon/ga.png");
}

html, body {
  max-width: 100%;
  overflow-x: hidden;
}

#breadcrumbs {
  display: none;
}

 #search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
     background: #FFF;
}

.rate-box-with-credit-button, .page-rate-widget-box {
  background-color: var(--main) !important;
  border: 1px solid var(--main) !important;
}

.rate-box-with-credit-button .creditButton p a {
  border-left-color: var(--main) !important;
  color: var(--font);
}

 h2, h3 {
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
}

 #header, #top-bar, #login-status, #search-top-box { z-index: unset; }
 div#extra-div-1, div#extra-div-2 { z-index: -1; }

 #header h1 a::before {
     color: #FFF;
     text-shadow: 0 -3px var(--main), 0 3px var(--main), 3px 0 var(--main), -3px 0 var(--main), 2px 2px var(--main), 2px -2px var(--main), -2px 2px var(--main), -2px -2px var(--main);
}

.block-center { margin-top: 2em; }

.mobile-top-bar { z-index: 4; }

#page-content ul {
  margin-bottom: 0;
}

#page-content .collapsible-block-folded .collapsible-block-link {
  margin-left: 36px;
  text-decoration: underline;
}

#page-content .collapsible-block-folded .collapsible-block-link::before {
  content: "";
  display: inline-block;
  position: relative;
  width: 4px;
  height: 4px;
  transform: translate( -7px, -2px );
  background: #333;
  box-shadow: 0 0 1px #888;
  z-index: -1;
}

#page-content .collapsible-block-unfolded {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, 0.7);
}

#page-content .collapsible-block-unfolded .collapsible-block-link {
  position: fixed;
  bottom: 50px;
  left: 50%;
  transform: translateX( -50% );
  word-break: break-all;
  z-index: 5;
}

#page-content .collapsible-block-content {
  position: fixed;
  top: -10px;
  bottom: -10px;
  left: 50%;
  transform: translateX( -50% );
  width: 90%;
  max-width: 700px;
  background: #F5F5F5;
  box-shadow: 0 0 20px #F5F5F5, 10px 0 20px #F5F5F5, -10px 0 20px #F5F5F5, 0 10px 20px #F5F5F5, 0 -10px 20px #F5F5F5, 10px 10px 20px #F5F5F5, -10px 10px 20px #F5F5F5, 10px -10px 20px #F5F5F5, -10px -10px 20px #F5F5F5;
}

.inbox {
  position: absolute;
  top: 20px;
  left: 0;
  right: 0;
  bottom: 100px;
  padding: 5px calc(3vw + 5px) 0;
  overflow-y: auto;
  scrollbar-width: thin;
  scrollbar-color: white transparent;
}

.inbox::-webkit-scrollbar {
  width: 5px;
}

.inbox::-webkit-scrollbar-thumb {
    background: white;
    border-radius: 2.5px;
}

.inbox::-webkit-scrollbar-track {
    background: transparent;
}

@media (max-width: 676px) {
  :root {
    --color-bg: no-repeat left center /auto 100% url("http://scpko.wdfiles.com/local--files/samcheonri-contest-gangwon/ga.png");
  }
}

