.ch_body {
    background: #f2f3f7;
    padding: 10px;
    border: 1px solid #b5b5b5;
}

.ch_buttons {
    padding: 20px 5px;
    border: 1px solid #e2e4e4;
    border-left: none;
    border-right: none;
    margin: 5px 0;
}

.ch_nums, .ch_bs {
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
}

.ch_bt {
    flex-grow: 1;
    text-align: center;
    width: calc(100% / 3);
    border: 1px solid #cecece;
    background: #fff;
    padding: 10px;
    box-sizing: border-box;
    cursor: pointer;
}

.ch_footer .ch_bt {
    width: calc(100% / 4);
}

.ch_nums {
    margin-bottom: 4px;
}

@media (max-width: 815px) {
#html-body .ch_bt {
    width: 100% ;  
}
}

.ch_bt {
    margin-top: -1px;
}

.ch_bt:hover {
    border-color: #fff;
}

.ch_post {
    border-style: none solid solid none;
    border-color: #ddd;
    border-width: 1px;
    background-color: #efefef;
    margin-bottom: 8px;
    padding: 8px;
    font-size: 120%;
    width: fit-content;
}

.ch_posts {
    display: flex;
    flex-direction: column;
    padding: 10px 0;
}

.ch_post-heade {
    font-size: 85%;
}

.ch_post-heade strong {
    font-size: 115%;
    color: #19812e;
}

.ch_post a {
    color: blue;
    text-decoration: underline;
}

.ch_post:last-of-type {
    margin-bottom: 0;
}

.ch_post a:hover {
    color: #5e62ff;
}

.ch_title {
    color: #485269;
    font-size: 18px;
    font-weight: bold;
    padding-bottom: 5px;
}

.ch_post img {
    max-width: 300px;
}

.ch_post-heade a {
    text-decoration: none;
    color: #000;
}

.ch_post-heade a:hover {
    color: red;
}

.ch_post:nth-of-type(1) .ch_post-heade a:before {content: '1'}
